body.messageBody:
{
    "msTeams": {
        "width": "full"
    },
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "type": "AdaptiveCard",
    "version": "1.2",
    "body": [
					{
						"type": "TextBlock",
						"size": "large",
						"color": "accent",
						"text": "**Azure Sentinel Alert - **@{triggerBody()?['object']?['properties']?['incidentNumber']} - @{triggerBody()?['object']?['properties']?['title']}",
						"wrap": true
					},
        {
            "type": "TextBlock",
            "text": "Please Select the playbooks to run for the incident:",
            "weight": "Bolder"
        },
        {
            "type": "TextBlock",
            "text": "Available tagged Logic App playbooks",
            "wrap": true,
            "weight": "Bolder",
            "color": "Accent"
        },
        {
            "type": "Input.ChoiceSet",
            "choices": @{body('LogicApp_-_Get_tagged_playbooks')},
            "placeholder": "IP Playbooks",
            "isMultiSelect": true,
            "style": "expanded",
            "id": "playbooks"
        }
    ],
    "actions": [
        {
            "type": "Action.Submit",
            "title": "Submit"
        }
    ]
}
