content.query:
let timeRange = 24h;
let recentData = GuardicorePolicyRules_CL
| where TimeGenerated >= ago(timeRange);

let max_ts = toscalar(
    recentData
    | summarize max(sampling_timestamp_d)
);

recentData
| where sampling_timestamp_d == max_ts
| where action_s contains "block"
| extend RulesetName = case(
    isempty(ruleset_name_s) or ruleset_name_s == "", "default",
    ruleset_name_s
)
| summarize TotalHitCount = sum(hit_count_d) by RulesetName
| top 10 by TotalHitCount desc
| project RulesetName, BlockingHits = TotalHitCount

let timeRange = 7d;
let startDate = endofday(ago(timeRange));
let endDate = endofday(now());
let all_days = range day from startDate to endDate step 1d;

let recentAppData = GuardicoreApplications_CL
| where TimeGenerated >= startDate
| extend sampling_ts = unixtime_seconds_todatetime(sampling_timestamp_d)
| extend day = endofday(sampling_ts);

// Get max sampling timestamp per day
let daily_max_ts = recentAppData
| summarize max_ts = max(sampling_ts) by day;

// Join raw data with daily max timestamps to get latest app data per day
let latest_data_per_day = recentAppData
| join kind=inner (daily_max_ts) on day, $left.sampling_ts == $right.max_ts;

// Count applications by status per day
let daily_counts = latest_data_per_day
| extend Status = case(
    tolower(state_s) contains "block", "Block",
    tolower(state_s) contains "alert", "Alert",
    "Other"
)
| where Status in ("Block", "Alert")
| summarize
    Block = dcountif(project_name_s, Status == "Block"),
    Alert = dcountif(project_name_s, Status == "Alert")
    by day;

all_days
| join kind=leftouter (daily_counts) on day
| extend Block = coalesce(Block, 0),
         Alert = coalesce(Alert, 0)
| where day > startDate and day != endDate
| project day = format_datetime(day, "yyyy-MM-dd"), Block, Alert
| sort by day asc

let recentData = GuardicorePolicyRules_CL
| where TimeGenerated >= ago(24h);

let max_ts = toscalar(
    recentData
    | summarize max(sampling_timestamp_d)
);

recentData
| where sampling_timestamp_d == max_ts
| summarize count = count() by action_s
| extend actionFormatted = replace("_", " ", tolower(action_s))
| extend actionFormatted = strcat(toupper(substring(actionFormatted, 0, 1)), substring(actionFormatted, 1))
| project Action = actionFormatted, Count = count
| order by Count desc

let startDate = endofday(ago(7d));
let endDate = endofday(now());
let all_days = range day from startDate to endDate step 1d;

let rawData = GuardicoreAgents_CL
| where TimeGenerated >= startDate
| extend sampling_ts = unixtime_seconds_todatetime(sampling_timestamp_d)
| extend day = endofday(sampling_ts);

// Step 1: Get max sampling timestamp per day
let daily_max_ts = rawData
| summarize max_ts = max(sampling_ts) by day;

// Step 2: Join raw data with daily max timestamps to get latest agent data per day
let latest_data_per_day = rawData
| join kind=inner (daily_max_ts) on day, $left.sampling_ts == $right.max_ts;

// Step 3: Count agents and protected agents per day
let daily_counts = latest_data_per_day
| summarize
    total_agents = count(),
    protected_agents = countif(health_enforcement_agent_status_s == "RUNNING" and is_agent_enforcing_b == true)
    by day;

all_days
| join kind=leftouter (daily_counts) on day
| extend total_agents = coalesce(total_agents, 0),
         protected_agents = coalesce(protected_agents, 0)
| where day > startDate and day != endDate
| project day = format_datetime(day, "yyyy-MM-dd"), total_agents, protected_agents
| sort by day asc

let timeRange = 24h;

let recentAgentData = GuardicoreAgents_CL
| where TimeGenerated >= ago(timeRange);

let max_ts_agents = toscalar(
    recentAgentData
    | summarize max(sampling_timestamp_d)
);

let agentCount = toscalar(
    recentAgentData
    | where sampling_timestamp_d == max_ts_agents
    | summarize count()
);

let protectedCount = toscalar(
    recentAgentData
    | where sampling_timestamp_d == max_ts_agents
    | where health_enforcement_agent_status_s == "RUNNING"
    | where is_agent_enforcing_b == true
    | summarize count()
);

print Category = "Total Workloads", Count = agentCount
| union (print Category = "Protected Workloads", Count = protectedCount)
