body.messageBody:
{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "TextBlock",
            "size": "large",
            "weight": "bolder",
            "text": "Suspicious IP - Microsoft Sentinel",
            "wrap": true
        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "TextBlock",
                            "text": "IP Address : @{triggerBody()?['Entity']?['properties']?['Address']}",
                            "wrap": true,
                            "weight": "Bolder"
                        },
                        {
                            "type": "TextBlock",
                            "weight": "bolder",
                            "text": "Virus Total Report",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Country :  @{body('Get_an_IP_report')?['data']?['attributes']?['country']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Continent :  @{body('Get_an_IP_report')?['data']?['attributes']?['continent']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "ASN :  @{body('Get_an_IP_report')?['data']?['attributes']?['asn']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "As_Owner : @{body('Get_an_IP_report')?['data']?['attributes']?['as_owner']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "weight": "bolder",
                            "text": "Total Votes :  ",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Malicious : @{body('Get_an_IP_report')?['data']?['attributes']?['last_analysis_stats']?['malicious']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Positive Votes : @{body('Get_an_IP_report')?['data']?['attributes']?['total_votes']?['harmless']} ",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Suspicious : @{body('Get_an_IP_report')?['data']?['attributes']?['last_analysis_stats']?['suspicious']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Number of reports saying that is Undetected : @{body('Get_an_IP_report')?['data']?['attributes']?['last_analysis_stats']?['undetected']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Network :  @{body('Get_an_IP_report')?['data']?['attributes']?['network']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "weight": "Bolder",
                            "text": "Incident configuration:",
                            "wrap": true
                        }
                    ],
                    "width": "auto"
                }
            ]
        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "Image",
                            "style": "Person",
                            "url": "https://connectoricons-prod.azureedge.net/releases/v1.0.1391/1.0.1391.2130/azuresentinel/icon.png",
                            "size": "Small"
                        }
                    ],
                    "width": "auto"
                }
            ]
        },
        {
            "type": "TextBlock",
            "text": "Close Microsoft Sentinel incident?"
        },
        {
            "choices": [
                {
                    "isSelected": true,
                    "title": "Close incident - False Positive",
                    "value": "False Positive - Incorrect Alert Logic"
                },
                {
                    "title": "Close incident - True Positive",
                    "value": "True Positive - Suspicious Activity"
                },
                {
                    "title": "Close incident - Benign Positive",
                    "value": "Benign Positive - Suspicious But Expected"
                },
                {
                    "title": "Undetermined",
                    "value": "Undetermined"
                }
            ],
            "id": "incidentStatus",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": "Benign Positive - Suspicious But Expected"
        },
        {
            "type": "TextBlock",
            "text": "Change Microsoft Sentinel incident severity?"
        },
        {
            "choices": [
                {
                    "title": "Medium",
                    "value": "Medium"
                },
                {
                    "title": "High",
                    "value": "High"
                },
                {
                    "title": "Low",
                    "value": "Low"
                },
                {
                    "title": "Don't change",
                    "value": "same"
                }
            ],
            "id": "incidentSeverity",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": ""
        },
        {
            "type": "TextBlock",
            "text": "Azure Firewall Actions",
            "size": "Medium",
            "weight": "Bolder"
        },
        {
            "type": "Image",
            "style": "Person",
            "url": "data:image/png;base64,",
            "size": "Small"
        }
    ],
    "actions": [
        {
            "type": "Action.ShowCard",
            "title": "Add this IP Address to the IP Group",
            "card": {
                "type": "AdaptiveCard",
                "body": [
                    {
                        "type": "TextBlock",
                        "text": "Select a Firewall and Rule collection: 

(Resource group - Firewall name - Collection name) "
                    },
                    {
                        "type": "Input.ChoiceSet",
                        "id": "FirewallRulesSelectedVal",
                        "label": "Please select Firewall and netRules name",
                        "style": "compact",
                        "isRequired": true,
                        "errorMessage": "This is a required input",
                         "placeholder": "Please select an Firewall - Network Rules List",
                         "isMultiSelect": true,
                        "choices": @{variables('FirewallsList')}
                    }
                ],
                "actions": [
                    {
                        "type": "Action.Submit",
                        "title": "OK"
                    }
                ],
                "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
                "version": "1.2"
            }
        },
        {
            "type": "Action.Submit",
            "title": "Ignore"
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2"
}
