datatable (Event:string)
    ["SQL Workbook"]
| extend database = (strcat("[", "{Databases}", "]"))
| extend database = todynamic(replace("'", '"', database))
| mvexpand database
| extend subscriptionId = extract(@"/subscriptions/([^/]+)", 1, tostring(database))
| summarize SQLs = count() by subscriptionId, DefenderForSQL = 0
| union
(
securityresources
| where type =~ "microsoft.security/pricings"
| where name == "SqlServers"
| project DefenderForSQL = iif(properties.pricingTier == 'Standard', 1, 0), SQLs = 0, subscriptionId
)
| summarize SQLs = sum(SQLs), DefenderForSQL = sum(DefenderForSQL) by subscriptionId
| project Subscription = strcat('/subscriptions/', subscriptionId), ["Databases"] = SQLs, ['Defender for SQL'] = iif(DefenderForSQL > 0,'yes','no'), ['Onboard Azure Defender'] = iif(DefenderForSQL > 0, '', 'https://ms.portal.azure.com/#blade/Microsoft_Azure_Security/SecurityMenuBlade/26')
| order by ['Defender for SQL'] asc

