datatable (Event:string)
    ["SQL Workbook"]
| extend databases = (strcat("[", "{Databases}", "]"))
| extend databases = todynamic(replace("'", '"', databases))
| mvexpand databases
| extend subscriptionId = extract(@"/subscriptions/([^/]+)", 1, tostring(databases))
| summarize SQLs = count() by subscriptionId, DefenderForSQL = 0
| union
(
securityresources
| where type =~ "microsoft.security/pricings"
| where name == "SqlServers"
| project DefenderForSQL = iif(properties.pricingTier == 'Standard', 1, 0), SQLs = 0, subscriptionId
)
| summarize SQLs = sum(SQLs), DefenderForSQL = sum(DefenderForSQL) by subscriptionId
| project Subscription = 1, ['Defender for SQL'] = iif(DefenderForSQL > 0,'Protected by Azure Defender','Not protected by Azure Defender')

