content.query:
BloodHoundLogs_CL
| where data_type == "finding_export"
| where created_at {time_range} 
| where domain_name startswith "{domain_name}"
 | where created_at {time_range} 
| summarize max(exposure/100) by bin(created_at, 1d), domain_name

BloodHoundLogs_CL
| where data_type == "finding_export"
| where created_at {time_range} 
| where domain_name startswith "{domain_name}"
 | where created_at {time_range} 
| summarize max(finding_count) by bin(created_at, 1d), domain_name
