union Corelight_v2_*_CL
| where ('*' in ({Sensor}) or _system_name_s in ({Sensor}))
| where TimeGenerated {GlobalTimeRestriction}
|make-series Trend = count() on TimeGenerated from {GlobalTimeRestriction:start} to {GlobalTimeRestriction:end} step {GlobalTimeRestriction:grain} by _path_s;

corelight_conn
| where TimeGenerated  {GlobalTimeRestriction}
//| where EventType startswith "conn"
| where isnotempty(service)
| summarize count() by tostring(service) | take 10

corelight_conn
| where TimeGenerated  {GlobalTimeRestriction}
//| where EventType startswith "conn"
| where isnotempty(id_resp_p)
| extend dstprt = tostring(toint(id_resp_p))
| summarize Count=count() by dstprt | sort by Count desc |take 10

corelight_conn
//| where TimeGenerated  {GlobalTimeRestriction}
//| where EventType startswith "conn"
//| extend NetworkDirection = case(LocalOrig == true,"outbound", LocalOrig == false, "inbound",'')
| where local_orig == true and local_resp == false
//| where isnotempty(SrcIpAddr) and isnotempty(DstIpAddr) and isnotempty(SrcIpBytes) and isnotempty(DstIpBytes)
| extend bytes = toint(orig_bytes) + toint(resp_bytes)
| summarize Bytes=sum(bytes) by id_orig_h, id_resp_h, proto | sort by Bytes desc | take 15

corelight_conn
//| where TimeGenerated  {GlobalTimeRestriction}
//| where EventType startswith "conn"
//| extend NetworkDirection = case(LocalOrig == true,"outbound", LocalOrig == false, "inbound",'')
| where local_orig == false and local_resp == true
//| where isnotempty(SrcIpAddr) and isnotempty(DstIpAddr) and isnotempty(SrcIpBytes) and isnotempty(DstIpBytes)
| extend bytes = toint(orig_bytes) + toint(resp_bytes)
| summarize Bytes=sum(bytes) by id_orig_h, id_resp_h, proto | sort by Bytes desc | take 15

corelight_conn
//| where EventType startswith "conn"
| where TimeGenerated  {GlobalTimeRestriction} 
| summarize Count=count() by id_orig_h | sort by Count

corelight_conn
//| where EventType startswith "conn"
| where TimeGenerated  {GlobalTimeRestriction} 
| summarize Count=count() by id_resp_h | sort by Count

corelight_conn
//| where EventType startswith "conn"
| where TimeGenerated  {GlobalTimeRestriction}
| where isnotempty(id_orig_h) and isnotempty(id_resp_h) and isnotempty(service) and isnotempty(id_orig_p) and isnotempty(id_resp_p)
| summarize Duration=avg(toint(duration)), make_list(id_orig_h), make_list(id_resp_h), make_list(proto) by uid | sort by Duration desc | take 50

corelight_files
| where TimeGenerated  {GlobalTimeRestriction}
//| where EventType startswith "files"
| where isnotempty(mime_type)
| where mime_type != "application/pkix-cert"
| summarize Count=count() by mime_type | sort by Count desc | take 20

corelight_files
| where TimeGenerated  {GlobalTimeRestriction}
//| where EventType startswith "files"
| where isnotempty(mime_type)
| where mime_type != "application/pkix-cert"
| summarize ["File Count"]=count() by source | sort by ["File Count"] desc | take 15

corelight_files
//| where EventType startswith "files"
| where isnotempty(mime_type)
| where mime_type != "application/pkix-cert"
//| extend NetworkDirection = case(local_orig == true, "outbound", local_orig == false, "inbound", "" )
// fixme: drop _d
|make-series ["Bytes Sent"]=sumif(toint(seen_bytes), local_orig == true), ["Bytes Received"]=sumif(toint(seen_bytes),local_orig == false) on TimeGenerated from {GlobalTimeRestriction:start} to {GlobalTimeRestriction:end} step {GlobalTimeRestriction:grain} by EventType

corelight_software
//| where EventType startswith "software"
| where TimeGenerated  {GlobalTimeRestriction}
//| where isnotempty(SoftwareType)
| summarize Count=count() by name | sort by Count | take 20

corelight_software
//| where TimeGenerated  {GlobalTimeRestriction}
//| where EventType startswith "software"
| where isnotempty(software_type)
| summarize Count=count() by name, unparsed_version | sort by Count

let interval_in_hrs= datetime_diff('hour', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let interval_in_days= datetime_diff('day', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let bin_duration=case(interval_in_hrs<=24, 1h, interval_in_days<=30, 1d, interval_in_days>=31 and interval_in_days<=90, 7d, 31d);
let SSL=corelight_ssl
| where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
| where is_self_signed=="yes" and is_dest_internal_ip=="true";
let trendline=toscalar(
SSL
| summarize arg_max(TimeGenerated, *) by ssl_subject_common_name
| make-series Trend = dcount(ssl_subject_common_name) default = 0 on TimeGenerated from {GlobalTimeRestriction:start} to {GlobalTimeRestriction:end} step bin_duration
| project Trend);
SSL
| summarize Count=dcount(ssl_subject_common_name)
| extend Trend = trendline

let interval_in_hrs= datetime_diff('hour', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let interval_in_days= datetime_diff('day', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let bin_duration=case(interval_in_hrs<=24, 1h, interval_in_days<=30, 1d, interval_in_days>=31 and interval_in_days<=90, 7d, 31d);
let X509=corelight_x509
| where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
| extend key_length = toint(certificate_key_length)
| where key_length < 2048;
let trendline=toscalar(
X509
| summarize arg_max(TimeGenerated, *) by ssl_hash
| make-series Trend = dcount(ssl_hash) default = 0 on TimeGenerated from {GlobalTimeRestriction:start} to {GlobalTimeRestriction:end} step bin_duration
| project Trend);
X509
| summarize Count = dcount(ssl_hash)
| extend Trend = trendline

let interval_in_hrs= datetime_diff('hour', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let interval_in_days= datetime_diff('day', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let bin_duration=case(interval_in_hrs<=24, 1h, interval_in_days<=30, 1d, interval_in_days>=31 and interval_in_days<=90, 7d, 31d);
corelight_ssl
| where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
| make-series ['Encrypted Traffic Volume']=count() default = 0 on TimeGenerated from {GlobalTimeRestriction:start} to {GlobalTimeRestriction:end} step bin_duration

let interval_in_hrs= datetime_diff('hour', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let interval_in_days= datetime_diff('day', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let bin_duration=case(interval_in_hrs<=24, 1h, interval_in_days<=30, 1d, interval_in_days>=31 and interval_in_days<=90, 7d, 31d);
let UnencryptedConnection=corelight_etc_viz
| where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
| where  viz_stat in ("C", "Cc", "C!", "cc");
let trendline=toscalar(
UnencryptedConnection 
| make-series Trend = count() default = 0 on TimeGenerated from {GlobalTimeRestriction:start} to {GlobalTimeRestriction:end} step bin_duration by server_a, server_p
| project Trend);
UnencryptedConnection 
| summarize Count = count() by server_a, server_p
| summarize Sum = sum(Count)
| extend Trend=trendline

let interval_in_hrs= datetime_diff('hour', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let interval_in_days= datetime_diff('day', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let bin_duration=case(interval_in_hrs<=24, 1h, interval_in_days<=30, 1d, interval_in_days>=31 and interval_in_days<=90, 7d, 31d);
let FailedDNS=corelight_dns
| where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
| where rcode_name in ('SERVFAIL', 'REFUSED', 'FORMERR' ,'NOTIMP' ,'NOTAUTH');
let trendline=toscalar(
FailedDNS
| make-series Trend = count() default = 0 on TimeGenerated from {GlobalTimeRestriction:start} to {GlobalTimeRestriction:end} step bin_duration
| project Trend);
FailedDNS
| summarize dns_fails = count()
| extend Trend = trendline

let interval_in_hrs= datetime_diff('hour', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let interval_in_days= datetime_diff('day', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let bin_duration=case(interval_in_hrs<=24, 1h, interval_in_days<=30, 1d, interval_in_days>=31 and interval_in_days<=90, 7d, 31d);
let UnusualQtypes=corelight_dns
| where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
| where qtype_name in ("AXFR", "IXFR", "ANY", "TXT");
let trendline=toscalar(
UnusualQtypes
| make-series Trend = count() default = 0 on TimeGenerated from {GlobalTimeRestriction:start} to {GlobalTimeRestriction:end} step bin_duration
);
UnusualQtypes
| summarize failed_q=count() by bin(TimeGenerated, 1d)
| summarize total = sum(failed_q) by TimeGenerated
| extend today = iff(TimeGenerated==(startofday(now())), total, 0)
| extend yesterday = iff(TimeGenerated==(startofday(now())-1d), total, 0)
| extend Trend = trendline
| serialize
| order by TimeGenerated desc
| extend nextyesterday = iff((today == 0 and yesterday > 0), yesterday, iff(isempty(next(yesterday)), 0, next(yesterday)))
| limit 1
| extend percentage = case(nextyesterday == 0 and today == 0, 0.0, 
                           nextyesterday == 0 and today !=0, todouble(today)*100, 
                           (todouble(today-nextyesterday)/nextyesterday)*100)

let interval_in_hrs= datetime_diff('hour', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let interval_in_days= datetime_diff('day', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let bin_duration=case(interval_in_hrs<=24, 1h, interval_in_days<=30, 1d, interval_in_days>=31 and interval_in_days<=90, 7d, 31d);
let NxdomainResponses=corelight_dns
| where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
| where rcode_name in ('NXDOMAIN', 'NOERROR');
let trendline=toscalar(
NxdomainResponses
| make-series Trend = count() default = 0 on TimeGenerated from {GlobalTimeRestriction:start} to {GlobalTimeRestriction:end} step bin_duration
);
NxdomainResponses
| summarize Count=count() by bin(TimeGenerated, 1d)
| summarize total = sum(Count) by TimeGenerated
| extend today = iff(TimeGenerated==(startofday(now())), total, 0)
| extend yesterday = iff(TimeGenerated==(startofday(now())-1d), total, 0)
| extend Trend = trendline
| serialize
| order by TimeGenerated desc
| extend nextyesterday = iff((today == 0 and yesterday > 0), yesterday, iff(isempty(next(yesterday)), 0, next(yesterday)))
| limit 1
| extend percentage = case(nextyesterday == 0 and today == 0, 0.0, 
                           nextyesterday == 0 and today !=0, todouble(today)*100, 
                           (todouble(today-nextyesterday)/nextyesterday)*100)

let interval_in_hrs= datetime_diff('hour', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let interval_in_days= datetime_diff('day', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let bin_duration=case(interval_in_hrs<=24, 1h, interval_in_days<=30, 1d, interval_in_days>=31 and interval_in_days<90, 7d, 31d);
corelight_vpn
| where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
| where is_dest_internal_ip == "false"
| make-series ["Outbound VPN Connections"]=count() default = 0 on TimeGenerated from {GlobalTimeRestriction:start} to {GlobalTimeRestriction:end} step bin_duration

let interval_in_hrs= datetime_diff('hour', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let interval_in_days= datetime_diff('day', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let bin_duration=case(interval_in_hrs<=24, 1h, interval_in_days<=30, 1d, interval_in_days>=31 and interval_in_days<=90, 7d, 31d);
corelight_rdp
| where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
| where isnotempty(auth_success)
| extend auth_result = iff(auth_success=="true","Success","Failure") 
| make-series Count=count() default = 0 on TimeGenerated from {GlobalTimeRestriction:start} to {GlobalTimeRestriction:end} step bin_duration by auth_result

let FilteredDNS = (
corelight_dns
| where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
| where qtype_name in ("AXFR", "IXFR", "ANY", "TXT")
);
let DNSRecords = (
FilteredDNS
| summarize count() by qtype_name, dest_ip
);
FilteredDNS
| join kind=leftouter(DNSRecords) on $left.qtype_name == $right.qtype_name, $left.dest_ip == $right.dest_ip
| summarize arg_max(TimeGenerated, *) by qtype_name, dest_ip
| project Qtype = qtype_name, Responder = dest_ip, Source = src_ip, Query = query, Count = count_
| sort by Count

let FilteredDNS = (
    corelight_dns
    | where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
    | where tolower(rcode_name) in ("servfail", "refused", "formerr", "notimp", "notauth")
    );
let RcodeCount = (
    FilteredDNS
    | summarize count() by rcode_name, dest_ip, src_ip
    );
FilteredDNS
| join kind=innerunique (RcodeCount) on rcode_name, dest_ip, src_ip
| project
    Source = src_ip,
    Responder = dest_ip,
    Query = query,
    ["Response Code"] = toupper(rcode_name),
    Count = count_
| sort by Count desc

let interval_in_hrs= datetime_diff('hour', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let interval_in_days= datetime_diff('day', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let bin_duration=case(interval_in_hrs<=24, 1h, interval_in_days<=30, 1d, interval_in_days>=31 and interval_in_days<=90, 7d, 31d);
corelight_dns
| where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
| summarize Count = count() by bin(TimeGenerated, bin_duration), uid

let interval_in_hrs= datetime_diff('hour', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let interval_in_days= datetime_diff('day', {GlobalTimeRestriction:end}, {GlobalTimeRestriction:start});
let bin_duration=case(interval_in_hrs<=24, 1h, interval_in_days<=30, 1d, interval_in_days>=31 and interval_in_days<=90, 7d, 31d);
corelight_rdp
| where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
| where action != "unknown"
| make-series Count=count() default = 0 on TimeGenerated from {GlobalTimeRestriction:start} to {GlobalTimeRestriction:end} step bin_duration by action

let FilteredRDP = (
corelight_rdp
| where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
| where action == "failure" or auth_success == "false"
| where isnotempty(cookie)
);
let CookieCount = (
FilteredRDP
| summarize Count = count() by cookie
);
let QueryResult = (
FilteredRDP
| join kind=leftouter(CookieCount) on $left.cookie == $right.cookie
| extend User = cookie, Source = src_ip, Responder = dest_ip, Auth_Success = tostring(auth_success), Result = result
| summarize arg_max(TimeGenerated, *) by User
| project User, Source, Responder, ['Auth Success'] = Auth_Success, Result, Count
| sort by Count
);
let QueryCount = (
QueryResult
| count
);
let NoResults = (
datatable ( User: string, Source: string, Responder: string, ['Auth Success']: string, Result: string, Count: long) [ "No Results", "N/A", "N/A", "N/A", "N/A", 0]
);
union isfuzzy=true 
(QueryResult| where toscalar(QueryCount) != 0),
(NoResults | where toscalar (QueryCount) == 0)

let FilteredVPN = (
    corelight_vpn
    | where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
    | where Inferences has_all ("COM", "RW", "NSP")
    );
let VPNCount = (
    FilteredVPN
    | extend Inferences = tostring(Inferences)
    | summarize Count = count() by src_ip, dest_ip, Inferences, vpn_type
    | project src_ip, dest_ip, Inferences, vpn_type, Count
    );
let QueryResults = (
FilteredVPN
| extend Inferences_string = tostring(Inferences)
| join kind=innerunique(VPNCount)
    on
    $left.src_ip == $right.src_ip,
    $left.dest_ip == $right.dest_ip,
    $left.Inferences_string == $right.Inferences,
    $left.vpn_type == $right.vpn_type
| extend
    Source = src_ip,
    Responder = dest_ip,
    ["VPN Type"] = vpn_type,
    Count
| extend NewInferences = strcat_array(Inferences, ",")
| project Source, Responder, Inferences = NewInferences, ['VPN Type'], Count
| sort by Count
);
let QueryCount = (
QueryResults
| count
);
let NoResults = (
datatable ( Source: string, Responder: string , Inferences: string, ['VPN Type']: string, Count: long)
["N/A", "N/A", "N/A", "N/A", 0]
);
union isfuzzy=true 
(QueryResults | where toscalar(QueryCount) != 0),
(NoResults | where toscalar(QueryCount) == 0)

let FilteredVPN = (
    corelight_vpn
    | where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
    | where Inferences has_any ("TLS", "SK")
    );
let VPNCount = ( 
    FilteredVPN
    | summarize Count = count() by src_ip, dest_ip
    );
let QueryResults = (
FilteredVPN
| extend inferences_string = tostring(replace(",", ":", strcat_array(Inferences, ":")))
| join kind=leftouter(VPNCount) on src_ip, dest_ip
| summarize arg_max(TimeGenerated, *) by src_ip, dest_ip
| project
    Source = src_ip,
    Responder = dest_ip,
    Inferences = inferences_string,
    ["Responder Country"] = resp_cc,
    ["VPN Type"] = vpn_type,
    Count
| sort by Count
);
let QueryCount = (
VPNCount
| count 
);
let NoResults = (
datatable ( Source: string, Responder: string, Inferences: string, ["Responder Country"]: string, ["VPN Type"]: string, Count: long)
["N/A", "N/A", "N/A", "N/A", "N/A", 0]
);
union isfuzzy=true 
(QueryResults | where toscalar(QueryCount) != 0),
(NoResults | where toscalar(QueryCount) == 0)
