let dummy_table = datatable(_system_name_s: string, TimeGenerated: datetime, _path_s: string)[];
union isfuzzy=true Corelight_v2_*_CL, dummy_table
| where ('*' in ({Sensor}) or _system_name_s in ({Sensor}))
| where TimeGenerated {GlobalTimeRestriction}
|make-series Trend = count() on TimeGenerated from {GlobalTimeRestriction:start} to {GlobalTimeRestriction:end} step {GlobalTimeRestriction:grain} by _path_s;

let dummy_table = datatable(_system_name_s: string, TimeGenerated: datetime, _path_s: string)[];
union isfuzzy=true Corelight_v2_*_CL, dummy_table
| where ('*' in ({Sensor}) or _system_name_s in ({Sensor}))
| where TimeGenerated {GlobalTimeRestriction}
| summarize Count=count() by _path_s | sort by Count desc
