let bin_duration=case(interval_in_hrs<=24, 1h, interval_in_days<=30, 1d, interval_in_days>=31 and interval_in_days<=90, 7d, 31d);
let UnusualQtypes=corelight_dns
| where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
| where qtype_name in ("AXFR", "IXFR", "ANY", "TXT");
let trendline=toscalar(
UnusualQtypes
| make-series Trend = count() default = 0 on TimeGenerated from {GlobalTimeRestriction:start} to {GlobalTimeRestriction:end} step bin_duration
);
UnusualQtypes
| summarize failed_q=count() by bin(TimeGenerated, 1d)
| summarize total = sum(failed_q) by TimeGenerated
| extend today = iff(TimeGenerated==(startofday(now())), total, 0)
| extend yesterday = iff(TimeGenerated==(startofday(now())-1d), total, 0)
| extend Trend = trendline
| serialize
| order by TimeGenerated desc
| extend nextyesterday = iff((today == 0 and yesterday > 0), yesterday, iff(isempty(next(yesterday)), 0, next(yesterday)))
| limit 1
| extend percentage = case(nextyesterday == 0 and today == 0, 0.0, 
                           nextyesterday == 0 and today !=0, todouble(today)*100, 
                           (todouble(today-nextyesterday)/nextyesterday)*100)

