    | where ('*' in ({Sensor}) or sensor_name in ({Sensor}))
    | where Inferences has_all ("COM", "RW", "NSP")
    );
let VPNCount = (
    FilteredVPN
    | extend Inferences = tostring(Inferences)
    | summarize Count = count() by src_ip, dest_ip, Inferences, vpn_type
    | project src_ip, dest_ip, Inferences, vpn_type, Count
    );
