Compose_Formatted_Comment.inputs:
@concat(
  '---\n**Entity:** ', coalesce(item()?['properties']?['address'], item()?['properties']?['hostName'], item()?['properties']?['url'], item()?['properties']?['mailboxPrimaryAddress'], item()?['properties']?['hashValue'], 'Unknown'), '\n',
  if(
    equals(body('Parse_IOC_Response')?['success'], true),
    concat(
      '**IOC:** ',
        if(
          equals(
            length(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]'))),
            0
          ),
          'No IOCs found.',
          first(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]')))?['ioc']
        ), '\n',
      '**Type:** ',
        if(
          equals(
            length(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]'))),
            0
          ),
          'N/A',
          first(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]')))?['ioc_type']
        ), '\n',
      '**Risk Score:** ',
        if(
          equals(
            length(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]'))),
            0
          ),
          'N/A',
          string(first(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]')))?['risk_score'])
        ), '\n',
      '**Confidence Rating:** ',
        if(
          equals(
            length(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]'))),
            0
          ),
          'N/A',
          first(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]')))?['confidence_rating']
        ), '\n',
      '**Sources:** ',
        if(
          equals(
            length(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]'))),
            0
          ),
          'N/A',
          join(first(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]')))?['sources'], ', ')
        ), '\n',
      '**Malware:** ',
        if(
          equals(
            length(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]'))),
            0
          ),
          'N/A',
          string(first(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]')))?['related_malware'])
        ), '\n',
      '**Threat Actors:** ',
        if(
          equals(
            length(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]'))),
            0
          ),
          'N/A',
          string(first(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]')))?['related_threat_actors'])
        ), '\n',
      '**Behavior Tags:** ',
        if(
          equals(
            length(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]'))),
            0
          ),
          'N/A',
          if(
            or(
              equals(first(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]')))?['behaviour_tags'], null),
              equals(first(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]')))?['behaviour_tags'], 'null')
            ),
            'None',
            join(first(coalesce(body('Parse_IOC_Response')?['data']?['iocs'], json('[]')))?['behaviour_tags'], ', ')
