inputs.body:
let start_date=datetime_add("day", 1, todatetime("@{variables('Timestamp')}"));
let end_date=datetime_add("day", -1,  todatetime("@{variables('Timestamp')}"));

SecurityAlert 
| where ProductName == "Azure Active Directory Identity Protection"
| where TimeGenerated between (start_date .. end_date) and 
    ExtendedProperties contains "@{body('Parse_Dynatrace_Attack_JSON')?['attacker']?['sourceIp']}"
| top 10 by TimeGenerated;
