inputs.body:
let start_date=datetime_add("minute", @{parameters('StartTimeOffsetInMinutes')}, todatetime("@{variables('Timestamp')}"));
let end_date=datetime_add("minute", @{parameters('EndTimeOffsetInMinutes')},  todatetime("@{variables('Timestamp')}"));
SecurityAlert
| where ProductName in ("Microsoft Defender Advanced Threat Protection","Office 365 Advanced Threat Protection","Azure Advanced Threat Protection","Microsoft Cloud App Security","Microsoft 365 Defender", "Azure Active Directory Identity Protection", "Azure Security Center")
| where ExtendedProperties has_any (@{join(variables('SearchTerms'),',')}) or Entities has_any (@{join(variables('SearchTerms'),',')})
| where TimeGenerated between (start_date .. end_date);
