let interval_in_hrs= datetime_diff('hour', {TimeRange:end}, {TimeRange:start});
let interval_in_days= datetime_diff('day', {TimeRange:end}, {TimeRange:start});
let bin_duration=case(interval_in_hrs<=24, 1h, interval_in_days<=30, 1d, interval_in_days>=31 and interval_in_days<=90, 7d, 31d);
SecurityAlert
| where "{AlertSeverity:label}" == "All" or AlertSeverity in ({AlertSeverity})
| where "{MITRETactic:label}" == "All" or Tactics contains ('{MITRETactic}')
| where"{MITRETechnique:label}" == "All" or Techniques has_any ('{MITRETechnique}')
| where ProductName == "ExtraHop"
| where AlertSeverity == '{AlertSeverityPicker}' or '{AlertSeverityPicker}' == "All"
| summarize count() by AlertSeverity, bin(TimeGenerated, bin_duration)
