#r "Newtonsoft.Json"
 #r "System.Net.Http"
 using System.Net;
 using Microsoft.AspNetCore.Mvc;
 using Microsoft.Extensions.Primitives;
 using Newtonsoft.Json;
 using Newtonsoft.Json.Linq;
 public static async Task<IActionResult> Run(HttpRequest req, ILogger log)
{
log.LogInformation("HTTP request recived.");
 var entity = req.Query["entity"];
var filter = req.Query["filter"];
var key = Environment.GetEnvironmentVariable("Authorization", EnvironmentVariableTarget.Process);
var endpointURL = Environment.GetEnvironmentVariable("EndpointURL", EnvironmentVariableTarget.Process);
var baseURL = Environment.GetEnvironmentVariable("BaseURL", EnvironmentVariableTarget.Process);
JToken results = null;
log.LogInformation(filter);
log.LogInformation(entity);
var httpclient = new HttpClient();
var httpRequest = new HttpRequestMessage(HttpMethod.Get, new Uri(endpointURL + baseURL + entity + "?" + filter ));
log.LogInformation(httpRequest.RequestUri.ToString());
 if (key.StartsWith("Bearer", StringComparison.InvariantCultureIgnoreCase)) {key = key.ToString() ;} else {key = "Bearer " + key.ToString();}
httpRequest.Headers.Add("Authorization", key.ToString());
var response = httpclient.SendAsync(httpRequest).Result;
var responseJson = response.Content.ReadAsStringAsync().Result;
if (responseJson != null)
{
JObject json = new JObject();
dynamic obj = JsonConvert.DeserializeObject(responseJson);
json = (JObject)obj;
results = json.SelectToken("results");
return new OkObjectResult(results);
}
return new OkObjectResult(results);
}

