variables.value:
.title {
    color: #1F2A47;
    font-size: 2rem;
    letter-spacing: .04em;
    padding: 8px;
    border-bottom: 1px solid #7B7B7B;
    font-weight: bold;
}

table  {
    border-collapse: collapse;
    border-spacing: 0;
    padding: 0;
    margin: 0 0 20px;
}

.tkey {
    text-align: right;
    border-right: 1px solid #7B7B7B;
}
.tvalue {
    text-align: left;
    border-top: 1px solid #eee !important;
    padding-left: 10px;
}
.value {
    font-size: 1.4rem;
    font-weight: bold;
}

.tkey, .key{
    color: #263137;
    letter-spacing: .04em;
    font-style: italic;
    padding-right: 10px;
}
.tvalue, .value {
    color: #1f4728 !important;
    letter-spacing: .02rem;
}
