{
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "actions": [
        {
            "title": "Submit",
            "type": "Action.Submit",
            "style": "positive",
            "id": "Submit",
"value":"Submit"
        }
    ],
    "body": [
        {
            "type": "TextBlock",
            "size": "large",
            "weight": "bolder",
            "horizontalAlignment": "center",
            "style": "heading",
            "color": "accent",
            "fontType": "Default",
            "wrap": true,
            "id": "heading",
            "text": "Block and Allow IP / Domain"
        },
        {
            "id": "group-choice",
            "type": "Input.ChoiceSet",
            "choices": @{variables('Lists')},
            "isRequired": true,
            "separator": true,
            "weight": "bolder",
            "label": "Select Named list to add/remove IP or Domain",
            "errorMessage": "Select one Named list first."
        },        {
            "id": "group-choice-1",
            "type": "Input.ChoiceSet",
            "choices": @{variables('List_action')},
            "isRequired": true,
           "weight": "bolder",
            "label": "Select Named list action",
            "errorMessage": "Select one Named list action."
        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "TextBlock",
                             "weight": "bolder",
                            "text": "List of IP / Domain to perform selected action for Named list",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "spacing": "None",
                            "text": "@{variables('ip_domain_string')}" ,
                            "isSubtle": true,
                            "wrap": true
                        }
                    ],
                    "width": "stretch"
                }
            ]
        }
    ],
    "type": "AdaptiveCard",
    "version": "1.3"
}

{
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "actions": [
        {
            "title": "Yes",
            "type": "Action.Submit",
            "style": "positive",
            "id": "Submit"
        },
        {
            "title": "No",
            "type": "Action.Submit",
            "style": "destructive",
            "id": "Cancel"
        }
    ],
    "body": [
        {
            "type": "TextBlock",
            "size": "large",
            "weight": "bolder",
            "horizontalAlignment": "center",
            "style": "heading",
            "color": "accent",
            "fontType": "Default",
            "wrap": true,
            "id": "heading",
            "text": "Block and Allow IP / Domain"
        },
                        {
                            "type": "TextBlock",
                            "weight": "Bolder",
                            "separator": true,
                            "text": "List of IP / Domain to be added in Named list",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "spacing": "None",
                            "text": "@{variables('ip_domain_string')}" ,
                            "isSubtle": true,
                            "wrap": true
                        },
                     {
                            "type": "TextBlock",
                            "size": "Medium",
                            "weight": "Bolder",
                            "text": "Want to add or remove above IP or Domain in Named list?"
                        }
    ],
    "type": "AdaptiveCard",
    "version": "1.3"
}

