let dummyschema = datatable(TimeGenerated:datetime, policyAnalyticsId_g:string, insightType_s:string, type_s:string)[];
union isfuzzy=true dummyschema,
@{variables('config_insights_table_name')}
| where TimeGenerated>ago(365d)
| project policyAnalyticsId_g
