properties.query:
// Time parameters provided by Hunting blade; include TI lookback (14d before start)
let starttime = todatetime('{{StartTimeISO}}');
let endtime   = todatetime('{{EndTimeISO}}');
let lookback  = starttime - 14d;
// Other parameters
let ipv4Regex = '[0-9]{1,3}\\.[0-9]{1,3}\\.[0-9]{1,3}\\.[0-9]{1,3}';
// 1) Select latest, active, non-expired Lumen IPv4 indicators
let IP_Indicators = ThreatIntelIndicators
  | where TimeGenerated between (lookback .. endtime)
  | where IsActive == true and ValidUntil > now()
  | summarize LatestIndicatorTime = arg_max(TimeGenerated, *) by Id
  | where SourceSystem == 'Lumen'
  | where ObservableKey == 'ipv4-addr:value'
  | extend TI_ipEntity = ObservableValue
  | where ipv4_is_private(TI_ipEntity) == false and TI_ipEntity !startswith '127.' and TI_ipEntity !startswith '::' and TI_ipEntity !startswith 'fe80';
// 2) Match indicators to CommonSecurityLog via SourceIP, DestinationIP, or an IP parsed from Message
IP_Indicators
| extend TI_ipEntity = tostring(TI_ipEntity)
| join kind=innerunique (
    CommonSecurityLog
    | where TimeGenerated between (starttime .. endtime)
    | extend MessageIP = extract(ipv4Regex, 0, Message)
    | extend CandidateIPs = pack_array(SourceIP, DestinationIP, MessageIP)
    | mv-expand CS_ipEntity = CandidateIPs
    | where isnotempty(CS_ipEntity)
    | extend CS_ipEntity = tostring(CS_ipEntity)
    | project-away CandidateIPs
    | extend CommonSecurityLog_TimeGenerated = TimeGenerated
  ) on $left.TI_ipEntity == $right.CS_ipEntity
| where CommonSecurityLog_TimeGenerated < ValidUntil
// 3) Keep the time range and latest matching event per indicator/IP and shape output
| summarize arg_max(CommonSecurityLog_TimeGenerated, *), StartTime = min(CommonSecurityLog_TimeGenerated), EndTime = max(CommonSecurityLog_TimeGenerated) by Id, CS_ipEntity
| project timestamp = EndTime, StartTime, EndTime, SourceIP, DestinationIP, MessageIP, Message, DeviceVendor, DeviceProduct, Id, Tags, ValidUntil, Confidence, TI_ipEntity, CS_ipEntity, LogSeverity, DeviceAction, Type
