//This query visualises total emails with Malware detections over time summarizing the data daily by Malware detection technologies/controls used for detecting unknown-unique malware.
let minTime = toscalar(EmailEvents | summarize min(Timestamp));
let maxTime = toscalar(EmailEvents | summarize max(Timestamp));
let baseQuery = EmailEvents
| where DetectionMethods has "Malware";
let fd=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Malware has 'File detonation' and Malware !has 'File detonation reputation'
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "File detonation";
let ud=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Malware has 'URL detonation' and Malware !has 'URL detonation reputation'
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "URL detonation";
union fd,ud
| project Count, Details, Timestamp

//This query visualises total emails with Phish detections over time summarizing the data daily.
//The comment in the query excludes deliveries to the SecOps Mailbox and by the Phish Simulation system. Remove the "//" to apply the exclusion.
EmailEvents
//| where OrgLevelPolicy != "Phishing simulation" and OrgLevelPolicy != "SecOps Mailbox"
| where ThreatTypes has "Phish"
| make-series PhishDetections = count() default = 0 on Timestamp step 1d

//This query visualises total emails with Phish detections summarizing the data by various Phish detection technologies/controls in Microsoft Defender for Office 365.
//The comment in the query excludes deliveries to the SecOps Mailbox and by the Phish Simulation system. Remove the "//" to apply the exclusion.
EmailEvents 
//| where OrgLevelPolicy != "Phishing simulation" and OrgLevelPolicy != "SecOps Mailbox"
| where ThreatTypes has 'Phish' 
| project Timestamp, DT=parse_json(DetectionMethods) 
| evaluate bag_unpack(DT) 
| summarize count() by Phish=tostring(column_ifexists('Phish', ''))
| sort by count_ desc

