//This query visualises where URL's have been identified in emails
EmailUrlInfo
| summarize  count() by UrlLocation, bin (Timestamp,1d)

// Generates a time-series analysis for blocked URL click events from the 'UrlClickEvents' table

let minTime = toscalar(EmailEvents | summarize min(Timestamp));
let maxTime = toscalar(EmailEvents | summarize max(Timestamp));

let baseQuery = UrlClickEvents
| where isnotempty(ActionType);

let Blocked = baseQuery
| where ActionType has_any("ClickBlocked")
| make-series Count = count() default = 0 on Timestamp from minTime to maxTime step 1d
| extend Details = "ClickBlocked";

Blocked

//This query visualises different URL click event types by action (e.g., blocked, allowed, pending verdict, error pages, and clicked through), summarizing the data daily

let minTime = toscalar(EmailEvents | summarize min(Timestamp));
let maxTime = toscalar(EmailEvents | summarize max(Timestamp));

let baseQuery = UrlClickEvents
| where isnotempty(ActionType);

let Blocked = baseQuery
| where ActionType has_any("ClickBlocked")
| make-series Count = count() default = 0 on Timestamp from minTime to maxTime step 1d
| extend Details = "ClickBlocked";

let Allowed = baseQuery
| where ActionType has_any("ClickAllowed")
| make-series Count = count() default = 0 on Timestamp from minTime to maxTime step 1d
| extend Details = "ClickAllowed";

let PendingVerdict = baseQuery
| where ActionType has_any("UrlScanInProgress")
| make-series Count = count() default = 0 on Timestamp from minTime to maxTime step 1d
| extend Details = "PendingVerdict";

let ErrorPage = baseQuery
| where ActionType has_any("UrlErrorPage")
| make-series Count = count() default = 0 on Timestamp from minTime to maxTime step 1d
| extend Details = "ErrorPage";

let ClickedThrough = baseQuery
| where IsClickedThrough == true
| make-series Count = count() default = 0 on Timestamp from minTime to maxTime step 1d
| extend Details = "ClickedThrough";

union Blocked, Allowed, PendingVerdict, ErrorPage, ClickedThrough

//This query visualises the daily ammount of admin false negative submission by submission type.
let minTime = toscalar(EmailEvents | summarize min(Timestamp));
let maxTime = toscalar(EmailEvents | summarize max(Timestamp));
let baseQuery = CloudAppEvents
| where ActionType contains "Submission"
| extend SubmissionType = tostring((parse_json(RawEventData)).SubmissionType),SubmissionContentType=tostring((parse_json(RawEventData)).SubmissionContentType);
let Admin_Malware_FN=baseQuery
| make-series Count= countif(SubmissionType == "2" and ActionType == "AdminSubmissionSubmitted" and SubmissionContentType=="Mail" ) default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Admin_Malware_FN";
let Admin_Phish_FN=baseQuery
| make-series Count= countif(SubmissionType == "1" and ActionType == "AdminSubmissionSubmitted" and SubmissionContentType=="Mail" ) default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Admin_Phish_FN";
let Admin_Spam_FN=baseQuery
| make-series Count= countif(SubmissionType == "0" and ActionType == "AdminSubmissionSubmitted" and SubmissionContentType=="Mail") default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Admin_Spam_FN";
let Admin_Malware_URL_FN=baseQuery
| make-series Count= countif(SubmissionType == "2" and ActionType == "AdminSubmissionSubmitted" and SubmissionContentType=="URL") default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Admin_Malware_URL_FN";
let Admin_Malware_Attach_FN=baseQuery
| make-series Count= countif(SubmissionType == "2" and ActionType == "AdminSubmissionSubmitted" and SubmissionContentType=="Attachment") default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Admin_Malware_Attach_FN";
let Admin_Phish_URL_FN=baseQuery
| make-series Count= countif(SubmissionType == "1" and ActionType == "AdminSubmissionSubmitted" and SubmissionContentType=="URL") default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Admin_Phish_URL_FN";
let Admin_Phish_Attach_FN=baseQuery
| make-series Count= countif(SubmissionType == "1" and ActionType == "AdminSubmissionSubmitted" and SubmissionContentType=="Attachment") default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Admin_Phish_Attach_FN";
union Admin_Malware_FN,Admin_Phish_FN,Admin_Spam_FN,Admin_Malware_URL_FN,Admin_Malware_Attach_FN,Admin_Phish_URL_FN,Admin_Phish_Attach_FN
| project Count, Details, Timestamp

