//This query visualises the daily ammount of emails that had a post delivery action from zero-hour auto purge.
EmailPostDeliveryEvents 
| where ActionType has "ZAP"
| make-series Count = count() default = 0 on Timestamp step 1d 
| extend Details = "Zapped Emails";

//This query visualises the daily ammount of emails that had a post delivery action from zero-hour auto purge, summarizing by phish,spam or malware detection action
let minTime = toscalar(EmailEvents | summarize min(Timestamp));
let maxTime = toscalar(EmailEvents | summarize max(Timestamp));
let baseQuery = EmailPostDeliveryEvents
| where ActionType has "ZAP";
let szap=baseQuery
| where ActionType has 'Spam ZAP' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Spam ZAP";
let pzap=baseQuery
| where ActionType has 'Phish ZAP' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Phish ZAP";
let mzap=baseQuery
| where ActionType has 'Malware ZAP' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Malware ZAP";
union szap,pzap,mzap
| project Count, Details, Timestamp

//This query visualises the daily ammount of emails that had an admin post delivery action, summarizing the data by action type
let minTime = toscalar(EmailEvents | summarize min(Timestamp));
let maxTime = toscalar(EmailEvents | summarize max(Timestamp));
let baseQuery = EmailPostDeliveryEvents
| where ActionTrigger has "AdminAction";
let sdelete=baseQuery
| where Action has 'Soft Delete' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Soft Delete";
let hdelete=baseQuery
| where Action has 'Hard Delete' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Hard Delete";
let mtojunk=baseQuery
| where Action has 'Moved to junk folder' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Moved to junk folder";
let mtoinbox=baseQuery
| where Action has 'Moved to inbox' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Moved to inbox";
let qrel=baseQuery
| where Action has 'Quarantine release' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Quarantine release";
union sdelete,hdelete,mtojunk,mtoinbox,qrel
| project Count, Details, Timestamp

//This query visualises the ammount of emails that had a post delivery action, summarizing the data daily by the final location as a result of the action
let minTime = toscalar(EmailEvents | summarize min(Timestamp));
let maxTime = toscalar(EmailEvents | summarize max(Timestamp));
let quarantine=EmailPostDeliveryEvents
| where DeliveryLocation has 'Quarantine' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Quarantine";
let delete=EmailPostDeliveryEvents
| where DeliveryLocation has 'Delete' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Delete";
let junk=EmailPostDeliveryEvents
| where DeliveryLocation has 'Junk' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Junk";
let inbox=EmailPostDeliveryEvents
| where DeliveryLocation has 'Inbox' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Inbox";
union quarantine,delete,junk,inbox
| project Count, Details, Timestamp

//This query visualises the daily ammount of emails released from quarantine
let minTime = toscalar(EmailEvents | summarize min(Timestamp));
let maxTime = toscalar(EmailEvents | summarize max(Timestamp));
let qrel = EmailPostDeliveryEvents
| where ActionTrigger has "AdminAction" and Action has 'Quarantine release' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Quarantine release";
qrel
| project Count, Details, Timestamp
