let ETSValidcontnet = union kind=outer (ExchangeConfiguration(SpecificSectionList="ExchangeServers",SpecificConfigurationDate="{DateOfConfiguration:value}",SpecificConfigurationEnv={EnvironmentList},Target = "On-Premises")| project Name = tostring(CmdletResultValue.Name)), (ExchangeConfiguration(SpecificSectionList="DAG",SpecificConfigurationDate="{DateOfConfiguration:value}",SpecificConfigurationEnv={EnvironmentList},Target = "On-Premises")| project Name = tostring(Identity));
ExchangeConfiguration(SpecificSectionList="ETS",SpecificConfigurationDate="{DateOfConfiguration:value}",SpecificConfigurationEnv={EnvironmentList},Target = "On-Premises")
| where CmdletResultValue.Name !in (ETSValidcontnet)
//| extend Name = strcat ("‚õî",tostring(CmdletResultValue.Name))
| extend Name = iff(CmdletResultType == "Success", strcat ("‚õî",tostring(CmdletResultValue.Name)),"Script was unable to retrieve data")
| project Name

let ETScontent = ExchangeConfiguration(SpecificSectionList="ETS", SpecificConfigurationDate="{DateOfConfiguration:value}",SpecificConfigurationEnv={EnvironmentList}, Target = "On-Premises") | project Name = tostring(CmdletResultValue.Name);
ExchangeConfiguration(SpecificSectionList="EWP",SpecificConfigurationDate="{DateOfConfiguration:value}",SpecificConfigurationEnv={EnvironmentList},Target = "On-Premises")
| where CmdletResultValue.Name !in (ETScontent) and CmdletResultValue.Name != "Exchange Trusted Subsystem"
//| extend Name = strcat ("‚õî",tostring(CmdletResultValue.Name))
| extend Name = iff(CmdletResultType == "Success", strcat ("‚õî",tostring(CmdletResultValue.Name)),"Script was unable to retrieve data")
| project Name
