body.query:
query{
  about{
    memory{
      rss
      heapTotal
      heapUsed
    }
  }
}

query{
  indicators(
first:100
 after:"@{variables('Cursor')}"
 orderBy:created
 orderMode:asc
  filters: 
    [ 
      {key: created_at values:"@{body('Parse_JSON')?['outputs']?['windowStartTime']}" operator:"gte"},
      {key: created_at values:"@{body('Parse_JSON')?['outputs']?['windowEndTime']}" operator:"lt"}
    ]
    filterMode:and
)
    { 
    edges {
      node {
        created_at
        entity_type
    pattern
    pattern_type
        x_opencti_main_observable_type
        name
        id
        created
        creator {         
          name
          id
        }
        createdBy {
          id
          name
          confidence
        }
        created_at
        indicator_types
        description
        x_opencti_score
        status{
          disabled
          id
          type
        }
        valid_from
        valid_until
      }
    }
    pageInfo {
      hasNextPage
      hasPreviousPage
      startCursor
      endCursor
      globalCount
    }
  }
}
