{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "TextBlock",
            "size": "large",
            "weight": "bolder",
            "text": "Suspicious Asset - Prisma cloud CSPM",
            "wrap": true
        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "TextBlock",
                            "text": "Asset Name : @{items('For_each_2_-_fetching_response_from_teams_channel_and_taking_action')['Resource Name']}",
                            "wrap": true,
                            "weight": "Bolder"
                        },
                        {
                            "type": "TextBlock",
                            "weight": "bolder",
                            "text": "Prisma CSPM Report",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Alert ID :  @{items('For_each_2_-_fetching_response_from_teams_channel_and_taking_action')['Alert id']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Policy Name : @{items('For_each_2_-_fetching_response_from_teams_channel_and_taking_action')['Policy Name']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Account :  @{items('For_each_2_-_fetching_response_from_teams_channel_and_taking_action')['Account']}",
                            "wrap": true
                        }
                    ],
                    "width": "auto"
                }
            ]
        },
        {
            "type": "TextBlock",
            "text": "Close Azure Sentinal incident?"
        },
        {
            "choices": [
                {
                    "isSelected": true,
                    "title": "Close incident - False Positive",
                    "value": "False Positive - Incorrect Alert Logic"
                },
                {
                    "title": "Close incident - True Positive",
                    "value": "True Positive - Suspicious Activity"
                },
                {
                    "title": "Close incident - Benign Positive",
                    "value": "Benign Positive - Suspicious But Expected"
                },
                {
                    "title": "Undetermined",
                    "value": "Undetermined"
                }
            ],
            "id": "incidentStatus",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": "Benign Positive - Suspicious But Expected"
        },
        {
            "type": "TextBlock",
            "text": "Change Microsoft Sentinel incident severity?"
        },
        {
            "choices": [
                {
                    "title": "Medium",
                    "value": "Medium"
                },
                {
                    "title": "High",
                    "value": "High"
                },
                {
                    "title": "Low",
                    "value": "Low"
                },
                {
                    "title": "Don't change",
                    "value": "same"
                }
            ],
            "id": "incidentSeverity",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": ""
        },
        {
            "type": "TextBlock",
            "text": "Provided Alert is not remediable by Prisma Cloud",
            "size": "Medium",
            "weight": "Bolder"
        }
    ],
"actions": [
        {
            "type": "Action.Submit",
            "title": "Submit",
        },
        {
            "type": "Action.Submit",
            "title": "Ignore"
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2"
}

