parameters.query:
{IP_Logs_Table:value}
| where TimeGenerated {IP_Logs_Time_Range:query}
| getschema
| where DataType == "System.String"
| project ColumnName

content.query:
ThreatIntelligenceIndicator
| where TimeGenerated {Threat_Intelligence_Time_Range:query}
| where Description contains {RF_Risk_list:value}
| where isnotempty(Tags)
| join (
    {IP_Logs_Table:value}
    | where TimeGenerated {IP_Logs_Time_Range:query}
    // renaming time column so it is clear the log this came from
    | extend IP_TimeGenerated = TimeGenerated
)
on $left.NetworkIP == $right.{IP_Logs_Field:value}
| summarize Correlation_Matches=count() by bin(IP_TimeGenerated, 1d)
| render barchart

ThreatIntelligenceIndicator
| where TimeGenerated {Threat_Intelligence_Time_Range:query}
| where Description contains {RF_Risk_list:value}
| where isnotempty(Tags)
| join (
    {IP_Logs_Table:value}
    | where TimeGenerated {IP_Logs_Time_Range:query}
    // renaming time column so it is clear the log this came from
    | extend IP_TimeGenerated = TimeGenerated
)
on $left.NetworkIP == $right.{IP_Logs_Field:value}
| project ["Risk Score"]=ConfidenceScore, IP=NetworkIP, Detected=format_datetime(TimeGenerated, 'yyyy-MM-dd HH:mm:ss'), ["Log Created"]=format_datetime(IP_TimeGenerated, 'yyyy-MM-dd HH:mm:ss'), ["Threat Classification"]=ThreatType
| summarize ["Log Created"]=max(["Log Created"]) by IP, ["Risk Score"], Detected, ["Threat Classification"]
| project ["Risk Score"], IP, Detected, ["Log Created"], ["Threat Classification"]
| sort by ["Risk Score"] desc

ThreatIntelligenceIndicator
| where TimeGenerated {Threat_Intelligence_Time_Range:query}
| where Description contains {RF_Risk_list:value}
| where isnotempty(Tags)
| join (
    {IP_Logs_Table:value}
    | where TimeGenerated {IP_Logs_Time_Range:query}
    // renaming time column so it is clear the log this came from
    | extend IP_TimeGenerated = TimeGenerated
)
on $left.NetworkIP == $right.{IP_Logs_Field:value}
| project Risk=ConfidenceScore, NetworkIP, ThreatType, Tags
| extend Evidence=parse_json(Tags)[0]
| mv-expand Evidence = parse_json(tostring(Evidence))
| extend Rule=Evidence['Rule']
| summarize count() by tostring(Rule)
| sort by count_ desc
