mutation StartBulkThreatHuntMutation($input: StartThreatHuntV2Input!) {
  startBulkThreatHunt(input: $input) {
    hunts {
      huntId
      huntName
      config {
        huntType
        clusterUuids
        objectFids
        __typename
      }
      status
      __typename
    }
    __typename
  }
}

query ThreatHuntDetailsV2Query($huntId: String!) {
  threatHuntObjectMetrics(huntId: $huntId) {
    totalObjectsScanned
    totalAffectedObjects
    totalUnaffectedObjects
    totalObjectsUnscannable
    unaffectedObjectsFromDb
    cleanRecoverableObjectLimit
    __typename
  }
  threatHuntDetailV2(huntId: $huntId) {
    totalObjectFids
    startTime
    endTime
    status
    totalMatchedSnapshots
    totalScannedSnapshots
    totalUniqueFileMatches
    baseConfig {
      name
      notes
      maxMatchesPerSnapshot
      threatHuntType
      ioc {
        iocList {
          indicatorsOfCompromise {
            iocKind
            iocValue
            __typename
          }
          __typename
        }
        __typename
      }
      snapshotScanLimit {
        scanLimit {
          scanConfig {
            maxSnapshotsPerObject
            startTime
            endTime
            __typename
          }
          objectSnapshotConfig {
            objectFid
            snapshotFid
            __typename
          }
          __typename
        }
        __typename
      }
      fileScanCriteria {
        fileSizeLimits {
          maximumSizeInBytes
          minimumSizeInBytes
          __typename
        }
        fileTimeLimits {
          earliestCreationTime
          earliestModificationTime
          latestCreationTime
          latestModificationTime
          __typename
        }
        pathFilter {
          inclusions
          exclusions
          exemptions
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
