query EventSeriesListQuery($after: String, $filters: ActivitySeriesFilter, $first: Int, $sortBy: ActivitySeriesSortField, $sortOrder: SortOrder) {
  activitySeriesConnection(
    after: $after
    first: $first
    filters: $filters
    sortBy: $sortBy
    sortOrder: $sortOrder
  ) {
    edges {
      cursor
      node {
        ...EventSeriesFragment
        cluster {
          id
          name
          __typename
        }
        activityConnection(first: 1) {
          nodes {
            id
            message
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    pageInfo {
      endCursor
      hasNextPage
      hasPreviousPage
      __typename
    }
    __typename
  }
}

fragment EventSeriesFragment on ActivitySeries {
  id
  fid
  activitySeriesId
  lastUpdated
  lastActivityType
  lastActivityStatus
  objectId
  objectName
  objectType
  severity
  progress
  isCancelable
  isPolarisEventSeries
  location
  __typename
}

query ObjectDetailQuery($snappableFid: String!, $snapshotFid: String!) {
                policyObj(snappableFid: $snappableFid, snapshotFid: $snapshotFid) {
                    id
                    rootFileResult {
                        hits {
                            totalHits
                    }
                    analyzerGroupResults {
                        analyzerGroup {
                            name
                        }
                        analyzerResults {
                            hits {
                                totalHits
                            }
                            analyzer {
                                name
                            }
                        }
                            hits {
                            totalHits
                        }
                    }
                    filesWithHits {
                        totalHits
                    }
                    openAccessFiles {
                        totalHits
                    }
                    openAccessFolders {
                        totalHits
                    }
                    openAccessFilesWithHits {
                        totalHits
                    }
                    staleFiles {
                        totalHits
                    }
                    staleFilesWithHits {
                        totalHits
                    }
                    openAccessStaleFiles {
                        totalHits
                    }
                    }
                }
            }

