query ObjectsListQuery($day: String!, $timezone: String!) {
  policyObjs(day: $day, timezone: $timezone,workloadTypes:[]) {
    edges {
      cursor
      node {
        ...PolicyObjFragment
        __typename
      }
      __typename
    }
    pageInfo {
      startCursor
      endCursor
      hasNextPage
      hasPreviousPage
      __typename
    }
    __typename
  }
}
fragment PolicyObjFragment on PolicyObj {
  id
  snapshotFid
  snapshotTimestamp
  osType
  shareType
  objectStatus {
    policyStatuses {
      status
      __typename
    }
    latestSnapshotResult {
      snapshotTime
      snapshotFid
      __typename
    }
    __typename
  }
  rootFileResult {
    hits {
      totalHits
      violations
      permittedHits
      violationsDelta
      totalHitsDelta
      __typename
    }
    analyzerGroupResults {
      ...AnalyzerGroupResultFragment
      __typename
    }
    filesWithHits {
      totalHits
      violations
      permittedHits
      violationsDelta
      totalHitsDelta
      __typename
    }
    openAccessFiles {
      totalHits
      violations
      permittedHits
      violationsDelta
      totalHitsDelta
      __typename
    }
    openAccessFolders {
      totalHits
      violations
      permittedHits
      violationsDelta
      totalHitsDelta
      __typename
    }
    openAccessFilesWithHits {
      totalHits
      violations
      permittedHits
      violationsDelta
      totalHitsDelta
      __typename
    }
    staleFiles {
      totalHits
      violations
      permittedHits
      violationsDelta
      totalHitsDelta
      __typename
    }
    staleFilesWithHits {
      totalHits
      violations
      permittedHits
      violationsDelta
      totalHitsDelta
      __typename
    }
    openAccessStaleFiles {
      totalHits
      violations
      permittedHits
      violationsDelta
      totalHitsDelta
      __typename
    }
    numActivities
    numActivitiesDelta
    __typename
  }
  snappable {
    ...SnappableFragment
    __typename
  }
  __typename
}
fragment AnalyzerGroupResultFragment on AnalyzerGroupResult {
  analyzerGroup {
    groupType
    id
    name
    __typename
  }
  analyzerResults {
    hits {
      totalHits
      violations
      permittedHits
      __typename
    }
    analyzer {
      id
      name
      analyzerType
      __typename
    }
    __typename
  }
  hits {
    totalHits
    violations
    permittedHits
    violationsDelta
    totalHitsDelta
    __typename
  }
  __typename
}
