query SnapshotsListSingleQuery($workloadId: String!, $first: Int, $after: String, $snapshotFilter: [SnapshotQueryFilterInput!], $sortBy: SnapshotQuerySortByField, $sortOrder: SortOrder, $timeRange: TimeRangeInput) {
  snapshotsListConnection: snapshotOfASnappableConnection(
    workloadId: $workloadId
    first: $first
    after: $after
    snapshotFilter: $snapshotFilter
    sortBy: $sortBy
    sortOrder: $sortOrder
    timeRange: $timeRange
  ) {
    edges {
      node {
        id
        date
        expirationDate
        isOnDemandSnapshot
        ... on CdmSnapshot {
          cdmVersion
          isDownloadedSnapshot
          cluster {
            id
            name
            version
            status
          }
          pendingSnapshotDeletion {
            id: snapshotFid
            status
          }
          slaDomain {
            name
            ... on ClusterSlaDomain {
              fid
              cluster {
                id
                name
              }
            }
            ... on GlobalSlaReply {
              id
            }
          }
          pendingSla {
            id
            name
          }
          snapshotRetentionInfo {
            archivalInfos {
              name
              isExpirationDateCalculated
              expirationTime
            }
            localInfo {
              name
              isExpirationDateCalculated
              expirationTime
            }
            replicationInfos {
              name
              isExpirationDateCalculated
              expirationTime
            }
          }
          sapHanaAppMetadata {
            backupId
            backupPrefix
            snapshotType
            files {
              backupFileSizeInBytes
            }
          }
          legalHoldInfo {
            shouldHoldInPlace
          }
        }
        ... on PolarisSnapshot {
          isDownloadedSnapshot
          isReplica
          isArchivalCopy
          slaDomain {
            name
            ... on ClusterSlaDomain {
              fid
              cluster {
                id
                name
              }
            }
            ... on GlobalSlaReply {
              id
            }
          }
        }
      }
    }
    pageInfo {
      endCursor
      hasNextPage
    }
  }
}

query GlobalSearchResults($first: Int, $after: String, $last: Int, $before: String, $sortBy: HierarchySortByField, $sortOrder: SortOrder, $filter: [Filter!]) {
  globalSearchResults(first: $first, after: $after, last: $last, before: $before, sortBy: $sortBy, sortOrder: $sortOrder, filter: $filter) {
    edges {
      cursor
      node {
        id
        name
        objectType
        slaAssignment
        slaPauseStatus
        numWorkloadDescendants
      }
    }
    nodes {
      id
      name
      objectType
      slaAssignment
      effectiveSlaDomain {
        id
        name
        version
      }
      slaPauseStatus
      snapshotDistribution {
        totalCount
        scheduledCount
        onDemandCount
        retrievedCount
        id
      }
      effectiveRetentionSlaDomain {
        id
        name
        version
      }
      configuredSlaDomain {
        id
        name
        version
      }
      effectiveSlaSourceObject {
        fid
        name
        objectType
      }
      logicalPath {
        fid
        name
        objectType
      }
      physicalPath {
        fid
        name
        objectType
      }
      numWorkloadDescendants
    }
    pageInfo {
      startCursor
      endCursor
      hasPreviousPage
      hasNextPage
    }
    count
  }
}

