query ThreatHuntMalwareResultQuery($huntId: String!) {
  threatHuntResult(huntId: $huntId) {
    huntId
    config {
      name
      indicatorsOfCompromise {
        iocValue
        iocKind
        __typename
      }
      __typename
    }
    results {
      object {
        id
        name
        objectType
        __typename
      }
      snapshotResults {
        snapshotId
        snapshotDate
        status
        lastJobId
        matches {
          indicatorIndex
          paths {
            requestedHashDetails {
              hashValue
              hashType
              __typename
            }
            yaraMatchDetails {
              name
              tags
              __typename
            }
            aclDetails
            creationTime
            modificationTime
            path
            __typename
          }
          __typename
        }
        quarantineDetails {
          filesDetails {
            fileName
            __typename
          }
          __typename
        }
        scanStats {
          numFiles
          numFilesScanned
          totalFilesScannedSizeBytes
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}

