{
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "type": "AdaptiveCard",
    "version": "1.3",
    "body": [
        {
            "type": "TextBlock",
            "size": "Large",
            "weight": "Bolder",
            "text": "IOC Scan Data to Perform File Recovery",
            "horizontalAlignment": "Center",
            "style": "heading",
            "color": "Accent",
            "fontType": "Default",
            "wrap": true,
            "id": "heading"
        },
        {
            "type": "TextBlock",
            "text": "Input arguments required to initiate IOC Scan(s) and option to recover file(s) of recoverable snapshot.",
            "wrap": true,
            "style": "default",
            "separator": true,
            "id": "info"
        },
		{
            "type": "TextBlock",
            "text": "Object Name =  @{variables('ObjectName')}\n\nIncident Start Time = @{variables('IncidentTime')}",
            "wrap": true,
            "style": "default",
            "color": "accent",
            "separator": true,
            "id": "x"
        },
        {
            "type": "TextBlock",
            "text": "IOC hash to scan for",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip1"
        },
        {
            "type": "Input.Text",
            "id": "IOC hash "
        },
        {
            "type": "TextBlock",
            "text": "IOC path to scan for",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip2"
        },
        {
            "type": "Input.Text",
            "id": "IOC path "
        },
        {
            "type": "TextBlock",
            "text": "IOC Yara rule to scan for",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip3"
        },
        {
            "type": "Input.Text",
            "id": "IOC Yara rule",
            "isMultiline": true
        },
        {
            "type": "TextBlock",
            "text": "Start date to initiate the IOC scan",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip4"
        },
        {
            "type": "Input.Date",
            "id": "Start date "
        },
        {
            "type": "TextBlock",
            "text": "End date to initiate the IOC scan",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip5"
        },
        {
            "type": "Input.Date",
            "id": "End date "
        },
        {
            "type": "TextBlock",
            "text": "Time Delta to update the date range of IOC scan",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip6"
        },
        {
            "type": "Input.Number",
            "id": "Time Delta "
        },
        {
            "type": "TextBlock",
            "text": "Polling interval for IOC Scan",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip7"
        },
        {
            "type": "Input.Number",
            "id": "Polling interval ",
            "placeholder": "5"
        },
        {
            "type": "TextBlock",
            "text": "Polling timeout for IOC Scan\n",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip8"
        },
        {
            "type": "Input.Number",
            "label": "In minutes",
            "id": "Polling timeout ",
            "placeholder": "720"
        },
        {
            "type": "TextBlock",
            "text": "Limit to loop the IOC scan sub-playbook",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip9"
        },
        {
            "type": "Input.Number",
            "id": "Limit to loop the IOC scan sub-playbook",
            "placeholder": "1"
        },
        {
            "type": "TextBlock",
            "text": "IOC Yara rule file URL to scan for",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip10"
        },
        {
            "type": "Input.Text",
            "id": "IOC Yara rule file URL ",
            "isMultiline": true
        },
        {
            "type": "TextBlock",
            "text": "Paths to include in the IOC Scan",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip11"
        },
        {
            "type": "Input.Text",
            "id": "Paths to include "
        },
        {
            "type": "TextBlock",
            "text": "Paths to exclude in the IOC Scan",
            "wrap": true,
            "separator": true,
            "weight": "Bolder",
            "id": "Ip12"
        },
        {
            "type": "Input.Text",
            "id": "Paths to exclude"
        },
        {
            "type": "TextBlock",
            "text": "Paths to exempt in the IOC Scan\n",
            "wrap": true,
            "weight": "Bolder",
            "id": "Ip13"
        },
        {
            "type": "Input.Text",
            "id": "Paths to exempt"
        },
        {
            "type": "TextBlock",
            "text": "Perform recovery",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip14"
        },
        {
            "type": "Input.ChoiceSet",
            "choices": [
                {
                    "title": "Yes",
                    "value": "Yes"
                },
                {
                    "title": "No",
                    "value": "No"
                }
            ],
            "id": "Perform recovery",
            "isRequired": true,
            "errorMessage": "Please select Performance recovery",
            "value": "Yes",
            "style": "expanded",
            "label": "required"
        },
        {
            "type": "TextBlock",
            "text": "Requested Hash Type",
            "wrap": true,
            "separator": true,
            "weight": "Bolder",
            "id": "Ip15"
        },
        {
            "type": "Input.ChoiceSet",
            "choices": [
                {
                    "title": "HASH_TYPE_M_D5",
                    "value": "HASH_TYPE_M_D5"
                },
                {
                    "title": "HASH_TYPE_SH_A256",
                    "value": "HASH_TYPE_SH_A256"
                }
            ],
            "value": "HASH_TYPE_M_D5",
            "id": "Requested Hash Type",
            "style": "expanded"
        },
        {
            "type": "TextBlock",
            "text": "Scan Name",
            "wrap": true,
            "separator": true,
            "weight": "Bolder",
            "id": "Ip16"
        },
        {
            "type": "Input.Text",
            "id": "Scan Name"
        }
    ],
    "actions": [
        {
            "type": "Action.Submit",
            "title": "Submit Answer",
            "data": {
                "id": "LoginVal"
            },
            "style": "positive",
            "id": "Submit"
        }
    ]
}

body.query:
mutation TakeVSphereSnapshotMutation($snappableId: String!, $slaID: String) {
    vsphereOnDemandSnapshot(input: { id: $snappableId, config: {slaId: $slaID} }) {
      id
      status
    }
  }

