body.query:
mutation RecoverVSphereVMFilesMutation($input: VsphereVmRecoverFilesNewInput!) {
  vsphereVmRecoverFilesNew(input: $input) {
    id
    status
    __typename
  }
}

query GetVSphereVmSnapshotInfoQuery($id: UUID!, $snapshotGroupBy: CdmSnapshotGroupByEnum!, $missedSnapshotGroupBy: MissedSnapshotGroupByTime!, $timeRange: TimeRangeInput!, $timezoneOffset: Float!, $clusterConnected: Boolean!) {
  snappable: vSphereVmNew(fid: $id) {
    id
    missedSnapshotGroupByConnection(
      groupBy: $missedSnapshotGroupBy
      filter: {timeRange: $timeRange}
      timezoneOffset: $timezoneOffset
    ) @include(if: $clusterConnected) {
      nodes {
        groupByInfo {
          ... on TimeRangeWithUnit {
            unit
            start
            end
            __typename
          }
          __typename
        }
        missedSnapshotConnection {
          count
          nodes {
            date
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    snapshotGroupByConnection(
      groupBy: $snapshotGroupBy
      filter: {timeRange: $timeRange}
      timezoneOffset: $timezoneOffset
    ) {
      nodes {
        groupByInfo {
          ... on TimeRangeWithUnit {
            unit
            start
            end
            __typename
          }
          __typename
        }
        snapshotConnection {
          count
          nodes {
            id
            isIndexed
            isUnindexable
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
