            "style": "default",
            "color": "accent",
            "separator": true,
            "id": "x"
        },
        {
            "type": "TextBlock",
            "text": "IOC hash to scan for",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip1"
        },
        {
            "type": "Input.Text",
            "id": "IOC hash to scan for"
        },
        {
            "type": "TextBlock",
            "text": "IOC path to scan for",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip2"
        },
        {
            "type": "Input.Text",
            "id": "IOC path to scan for"
        },
        {
            "type": "TextBlock",
            "text": "IOC Yara rule to scan for",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip3"
        },
        {
            "type": "Input.Text",
            "id": "IOC Yara rule to scan for",
            "isMultiline": true
        },
        {
            "type": "TextBlock",
            "text": "IOC Yara rule file URL to scan for",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip4"
        },
        {
            "type": "Input.Text",
            "id": "IOC Yara rule file URL"
        },
        {
            "type": "TextBlock",
            "text": "Start date to initiate the IOC scan",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip5"
        },
        {
            "type": "Input.Date",
            "id": "Start date to initiate"
        },
        {
            "type": "TextBlock",
            "text": "End date to initiate the IOC scan",
            "wrap": true,
            "weight": "Bolder",
            "separator": true,
            "id": "Ip6"
        },
        {
            "type": "Input.Date",
            "id": "End date to initiate"
        },
        {
            "type": "TextBlock",
            "text": "Time Delta to update the date range of IOC scan",
            "wrap": true,
            "weight": "Bolder",
            "id": "Ip7"
        },
        {
            "type": "Input.Number",
            "id": "Time Delta"
        },
        {
            "type": "TextBlock",
            "text": "Polling interval for IOC Scan",
            "wrap": true,
            "weight": "Bolder",
            "id": "Ip8"
        },
        {
            "type": "Input.Number",
            "placeholder": "5",
            "id": "Polling interval for IOC Scan"
        },
        {
            "type": "TextBlock",
            "text": "Polling timeout for IOC Scan",
            "wrap": true,
            "weight": "Bolder",
            "id": "Ip9"
        },
        {
            "type": "Input.Number",
            "placeholder": "720",
            "label": "In minutes",
            "id": "Polling timeout for IOC Scan"
        },
        {
            "type": "TextBlock",
            "text": "Polling interval for recovery asynchronous results",
            "wrap": true,
            "weight": "Bolder",
            "id": "Ip10"
        },
        {
            "type": "Input.Number",
            "placeholder": "5",
            "id": "Polling interval for recovery"
        },
        {
            "type": "TextBlock",
            "text": "Polling timeout for recovery asynchronous results",
            "wrap": true,
            "weight": "Bolder",
            "id": "Ip11"
        },
        {
            "type": "Input.Number",
            "placeholder": "720",
            "id": "Polling timeout for recovery",
            "label": "In minutes"
        },
        {
            "type": "TextBlock",
            "text": "Limit to loop the IOC scan sub-playbook",
            "wrap": true,
            "weight": "Bolder",
            "id": "Ip12"
        },
        {
            "type": "Input.Number",
            "placeholder": "0",
            "id": "Limit to loop the IOC scan sub-playbook"
        },
        {
            "type": "TextBlock",
            "text": "Paths to include in the IOC Scan",
            "wrap": true,
            "weight": "Bolder",
            "id": "Ip13"
        },
        {
            "type": "Input.Text",
            "id": "Paths to include in the IOC Scan"
        },
        {
            "type": "TextBlock",
            "text": "Paths to exclude in the IOC Scan",
            "wrap": true,
            "weight": "Bolder",
            "id": "Ip14"
        },
        {
            "type": "Input.Text",
            "id": "Paths to exclude in the IOC Scan"
        },
        {
            "type": "TextBlock",
            "text": "Paths to exempt in the IOC Scan",
            "wrap": true,
            "weight": "Bolder",
            "id": "Ip15"
        },
        {
            "type": "Input.Text",
            "id": "Paths to exempt in the IOC Scan"
        },
        {
            "type": "TextBlock",
            "text": "Whether to live-mount or export a recoverable snapshot?",
            "wrap": true,
            "weight": "Bolder",
            "id": "Ip16"
        },
        {
            "type": "Input.ChoiceSet",
            "id": "live-mount or export",
            "value": "Live-Mount",
            "style": "expanded",
            "choices": [
                {
                    "title": "Live-Mount",
                    "value": "Live-Mount"
                },
                {
                    "title": "Export",
                    "value": "Export"
                },
                {
                    "title": "No Recovery",
                    "value": "No Recovery"
                }
            ],
            "isRequired": true,
            "errorMessage": "This field id required",
            "label": "required"
        },
        {
            "type": "TextBlock",
            "text": "Scan Name",
            "wrap": true,
            "weight": "Bolder",
            "id": "Ip17"
        },
        {
            "type": "Input.Text",
            "id": "Scan Name"
        }
    ],
    "actions": [
        {
            "type": "Action.Submit",
            "title": "Submit Answer",
            "data": {
                "id": "LoginVal"
            },
            "style": "positive",
            "id": "Submit"
        }
    ]
}

