query vSphereHostsQuery($first: Int!, $after: String, $filter: [Filter!], $sortBy: HierarchySortByField, $sortOrder: SortOrder) {
  vSphereHostConnection(
    first: $first
    after: $after
    filter: $filter
    sortBy: $sortBy
    sortOrder: $sortOrder
  ) {
    edges {
      node {
        id
        ...VSphereHostNameFragment
        ...VSphereHostPhysicalPathFragment
        __typename
      }
      __typename
    }
    pageInfo {
      endCursor
      hasNextPage
      hasPreviousPage
      __typename
    }
    __typename
  }
}
 
fragment VSphereHostNameFragment on VsphereHost {
  name
  __typename
}
 
fragment VSphereHostPhysicalPathFragment on VsphereHost {
  physicalPath {
    fid
    name
    objectType
    __typename
  }
  __typename
}

mutation vSphereLiveMountMutation($snappableId: String!, $hostId: String, $clusterId: String, $resourcePoolId: String, $snapshotFid: String, $shouldRecoverTags: Boolean!, $keepMacAddresses: Boolean!, $powerOn: Boolean!, $removeNetworkDevices: Boolean!, $vmName: String, $vNicBindings: [VmwareVnicBindingInfoV2Input!], $recoveryPoint: DateTime, $shouldMigrateImmediately: Boolean!, $migrationConfig: RelocateMountConfigV2Input) {
  vsphereVmInitiateLiveMountV2(
    input: {
      id: $snappableId, config: { hostId: $hostId, resourcePoolId: $resourcePoolId, clusterId: $clusterId, shouldRecoverTags: $shouldRecoverTags, vNicBindings: $vNicBindings, requiredRecoveryParameters: { snapshotId: $snapshotFid, recoveryPoint: $recoveryPoint}mountExportSnapshotJobCommonOptionsV2: { keepMacAddresses: $keepMacAddresses, powerOn: $powerOn, removeNetworkDevices: $removeNetworkDevices, vmName: $vmName}, shouldMigrateImmediately: $shouldMigrateImmediately, migrationConfig: $migrationConfig}}
  ) {
      id
    status
    __typename
  }
}

