query VSphereHostDatastoresQuery($hostId: UUID!, $first: Int, $after: String, $filter: [Filter!], $sortBy: HierarchySortByField, $sortOrder: SortOrder) {
  vSphereHost(fid: $hostId) {
    descendantConnection(
      first: $first
      after: $after
      filter: $filter
      sortBy: $sortBy
      sortOrder: $sortOrder
      typeFilter: [VSphereDatastore]
    ) {
      edges {
        node {
          id
          ...HierarchyObjectNameColumnFragment
          ... on VsphereDatastore {
            capacity
            isLocal
            freeSpace
            datastoreType
            __typename
          }
          __typename
        }
        __typename
      }
      pageInfo {
        endCursor
        hasNextPage
        hasPreviousPage
        __typename
      }
      __typename
    }
    __typename
  }
}
 
fragment HierarchyObjectNameColumnFragment on HierarchyObject {
  name
  __typename
}

query vSphereHostsQuery($first: Int!, $after: String, $filter: [Filter!], $sortBy: HierarchySortByField, $sortOrder: SortOrder) {
  vSphereHostConnection(
    first: $first
    after: $after
    filter: $filter
    sortBy: $sortBy
    sortOrder: $sortOrder
  ) {
    edges {
      node {
        id
        ...VSphereHostNameFragment
        ...VSphereHostPhysicalPathFragment
        __typename
      }
      __typename
    }
    pageInfo {
      endCursor
      hasNextPage
      hasPreviousPage
      __typename
    }
    __typename
  }
}
 
fragment VSphereHostNameFragment on VsphereHost {
  name
  __typename
}
 
fragment VSphereHostPhysicalPathFragment on VsphereHost {
  physicalPath {
    fid
    name
    objectType
    __typename
  }
  __typename
}
