properties.query:
//  a per SAP note https://launchpad.support.sap.com/#/notes/3089413
// Systems without this parameter, or with values != "no" are vulnerable
// both (default) - old method allowed as client and server
// client - old method allowed as client
// server - old method allowed as server
// no - old method not allowed
// CONFIGURATION OPTION- determine which system roles are relevant for this alert
let TimeAgo= 7d;
let SelectedSystemRoles =  dynamic(["All System Roles"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles) | project SystemID= SearchKey, SystemRole, SystemUsage
| extend SystemRole= replace_string(SystemRole, 'Unknown (Production)', 'Unknown' );
let Vulnerability= 'SAP - [CVE-2023-0014] Capture-replay vulnerability in a ';
let Remediation= ' SAP NetWeaver ABAP Server and ABAP Platform creates information about system identity in an ambiguous format. <br/> This may be exploited by malicious users to obtain illegitimate access to the system. <br/> Read SAP note 3281854 - FAQ for Security Note 3089413 for more details.';
let SafeValues= dynamic(['no']);
let MitigatedValues= dynamic(['client']);
let ConfiguredSystems= SAP_PAHI
| where TimeGenerated > ago(TimeAgo)
| where PARNAME == 'rfc/allowoldticket4tt'
| where PARSTATE == "A"
| summarize arg_max(PARDATE, PARVALUE, TimeGenerated, SYSTEMID) by SystemID, Host= HOSTNAME, SYSTEMID
| project-rename SystemNumber= SYSTEMID
| extend LastChanged= todatetime(PARDATE);
// ConfiguredSystems
let PotentialSystems= SAPAuditLog
| where TimeGenerated > ago(TimeAgo)
| project SystemID, Host, SystemNumber
| summarize by SystemID, Host, SystemNumber
| join kind= inner SelectedSystems on SystemID;
PotentialSystems | join kind= inner ConfiguredSystems on SystemID, Host, SystemNumber
| summarize SystemRole=any(SystemRole), Hosts= makeset(Host), SystemNumbers= makeset(SystemNumber) by SystemID, PARVALUE
| extend BagODetails= case(
(PARVALUE in (MitigatedValues)),bag_pack('AlertName', strcat(Vulnerability, SystemRole, ' system- mitigated' ),'AlertDescription', Remediation, 'IsVulnerable', true, 'Severity', 'Medium' ),
(PARVALUE in (SafeValues)),bag_pack('AlertName', strcat(Vulnerability, SystemRole, ' - system resolved' ),'AlertDescription', Remediation, 'IsVulnerable', false, 'Severity', 'Iformational' ),
//  else:
bag_pack('AlertName', strcat(Vulnerability, SystemRole, ' system'),'AlertDescription', Remediation, 'IsVulnerable', true, 'Severity', 'High' ))
| evaluate bag_unpack(BagODetails)
| project Hosts, SystemNumbers, PARVALUE, AlertName= columnifexists('AlertName', Vulnerability), AlertDescription= columnifexists('AlertDescription', Remediation), SystemID, Severity= columnifexists('Severity', '')
| extend ExtendedLink= 'https://www.cve.org/CVERecord?id=CVE-2023-0014', Dummy= ''

// SAP - Medium - Critical authorizations assignment - New Authorization Value
// time span for audit events
let TimeAgo= 6h;
// New Assigned Objects
let ObjectClassRoles = 'PFCG';
let TableName = 'CD1251';
let UsersRoles = 'AGR_USERS';
let Insert = "I";
let NotInUse = 'NOT_IN_USE';
let logsThreshold = 3600; // 3600 seconds
// Audit Log Classes - Authorizations for user changed
let AuditClasses = dynamic(['AUR','AUT']); // Authorization/Authorization Profile &B created / changed.
// Roles Change Documents - Extract Auth Object and Obj Field
let allHistory = ago(0d);
let alertSched = ago(6h); // Please maintain according to schedule
let ChangeDocs = SAPChangeDocsLog;
let UsersEmails = SAPUsersHeader()| summarize by User, Email, SystemID, Client | project-rename ClientID = Client;
let RolesAuthObject = ChangeDocs
    | where TimeGenerated > ago(TimeAgo)
    | where TimeGenerated >= alertSched
    | where ObjectClass == ObjectClassRoles and TableName == TableName // Role-Obj-Profile-ObjField
    | where TypeofChange_Item in ('J', 'I', 'U') //  Insert
    | extend RoleObjProfileObjFieldVer = ChangedTableKey, Role = ObjectID
    | extend ObjFieldValue = ValueNew
    | extend ObjField = trim(@"\s*?", extract(@"(^.{1,30})\s*?(.{1,10})\s*?(.{1,12})\s*?(.{1,10})\s*?\d{6}", 4, RoleObjProfileObjFieldVer, typeof(string)))
    | extend AuthObject = trim(@"\s*?", extract(@"(^.{1,30})\s*?(.{1,10})\s*?(.{1,12})\s*?(.{1,10})\s*?\d{6}", 2, RoleObjProfileObjFieldVer, typeof(string)))
    | extend TimeGenRoleAuth = TimeGenerated;
let ComplexAuth = _GetWatchlist('SAP - Critical Authorizations');
let SimpleAuth = _GetWatchlist('SAP - Critical Authorizations');
let usersinRole =
    ChangeDocs
    | where TimeGenerated > ago(TimeAgo)
    | where TimeGenerated <= allHistory
    | where ObjectClass == ObjectClassRoles // Roles
        and TableName == UsersRoles // Users Roles
        and TypeofChange_Item == Insert // Insert
    | extend UserAssigned = extract(@"^.{1,33}\s*?(.{1,12})\s*?\d{16}", 1, ChangedTableKey)
    | extend Role = ObjectID
    | summarize by SystemID, Role, UserAssigned;
let RolesAuthObjectCheck =
        RolesAuthObject
        | extend ObjFieldVal = ObjFieldValue
        | lookup kind = leftouter
            (RolesAuthObject
            | extend ActivityVal = ObjFieldValue)
            on Role, AuthObject;
let complexScenario = ComplexAuth
    | where ActivityField != NotInUse
    | summarize by AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity
    | lookup kind = inner (RolesAuthObjectCheck)
        on $left.AuthorizationObject == $right.AuthObject
        and $left.AuthorizationField == $right.ObjField
        and $left.AuthorizationValue == $right.ObjFieldValue
        and $left.ActivityField == $right.ObjField1
        and $left.Activity == $right.ActivityVal;
let simpleScenario = SimpleAuth
    | where ActivityField == NotInUse
    | summarize by AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity
    | lookup  kind = inner (RolesAuthObject)
        on $left.AuthorizationObject == $right.AuthObject
        and $left.AuthorizationField == $right.ObjField
        and $left.AuthorizationValue == $right.ObjFieldValue;
let GetEntities =
    SAPAuditLog
    | where MessageID in (AuditClasses)
    | where TimeGenerated > ago(TimeAgo)
    | summarize by TimeGenerated, ClientID, TerminalIPv6, User, Host, Email
    | extend TimeGenAudit = TimeGenerated;
union complexScenario, simpleScenario
| lookup kind = inner (usersinRole) on SystemID, Role
| lookup kind = leftouter (GetEntities) on User
| where abs(datetime_diff('second', TimeGenRoleAuth, TimeGenAudit)) <= logsThreshold or
isnull(TimeGenAudit)
| lookup UsersEmails on SystemID, ClientID, User
| project
TimeGenRoleAuth, SystemID, ClientID, Role, User, UserAssigned, AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity, Email, TerminalIPv6, Host, UserAssignedEmail=Email1

// ***IMPORTANT***- Lookback period is maintained in the KQL
// please keep the Query scheduling parameter of "Lookup data from the last" to be 14 days so
// the algorithm can detect anomalies across history
let RedAgo = 2d;
let YellowAgo = 1d;
let GreenAgo= 1h;
let LogLookBack= 14d;
let StepSize= 1d;
// 0.1 is very sensitive and is required for low activity workspaces with under 1M events a day.
// 0.5 can work better for high scale WSs (between 1M and 10M records a day). 1 can be used for WSs with over 10m records a day
let Threshold= 0.1;
let SystemLevelIssues= SAPConnectorHealth()
    | summarize arg_max(LastSeen, *) by SystemID // last status per SystemID
    | where SystemID !in (dynamic(["N/A", "AGE", "Agent Version Check"])) // agents checks are not interesting
    | where MessageCode != "OKOK" // only show problems
    | where Status == "Yellow" or Status == "Red"
    | extend Severity= case(Status == "Yellow", "Medium", Status == "Red", "High", "Unknown")
    | extend Log= 'Entire system'
    | extend Details= replace_string(Details, "System", strcat("SAP System ", SystemID));
let LogContinuationTS= union SAPConnectorOverview()
    | where TimeGenerated > ago(LogLookBack)
    | project Type, TimeGenerated, SystemID= SystemID_s
    | make-series RecordCount= count(), LastSeen= max(TimeGenerated) on TimeGenerated from ago(LogLookBack) to now() step StepSize by Log= Type, SystemID;
let LogContinuationAnomal= LogContinuationTS
    | extend (RecordCountAnomalies, score, baseline) = series_decompose_anomalies(RecordCount, Threshold= Threshold, Test_points= 1)
    | where array_index_of(RecordCountAnomalies, -1, -1) > 0 // some log is missing in the last bucket
    | extend Severity= iff(RecordCount[-1] == 0, "Low", "Informational") // determine severity according to certainty
    | extend Details= strcat('SAP- Potential log loss for log ', Log, ' from System ', SystemID)
    | extend LastSeen= todatetime(LastSeen[-1])
    | extend ExtendedDetails= strcat('Log ', Log, ' from System ', SystemID, ' has produced ', RecordCount[-1], ' recods since ', format_datetime(todatetime(TimeGenerated[-1]), 'yy-MM-dd [hh:mm:ss tt]'), '. Expected number of records is ', round(toint(baseline[-1]), 0));
(LogContinuationAnomal
| join kind=leftantisemi (SystemLevelIssues
    | where Status == "Red"
    | summarize by SystemID)
    on SystemID) // System-wide issues should overtake log level issues
| union SystemLevelIssues
| where Severity != "Informational" // optionally can have these cases produce alerts by deleting this filter
| project Log, SystemID, LastSeen, Severity, Details, ExtendedDetails
    , Agent, MessageCode, AgentVersion
| extend Dummy= ''

// sensitive data was released using transport system
// here you can exclude system users which are OK to export sensitive data using transport system
// by adding those users in the SAP_User_Config watchlist with a tag of 'ExportSensitiveDataOK'
// can also specify SAP Roles or SAP Profiles to exclude
let excludeUsersTags= dynamic(['ExportSensitiveDataOK']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
let SelectedSystemRoles =  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles) | project SystemID= SearchKey;
let SensitiveTables = _GetWatchlist('SAP - Sensitive Tables') | summarize by TableName= Table | extend IsSensitive= true;
let TransportReleaseTCODEs= dynamic(["SE10", "SE01"]);
let TransportReleasePrograms= dynamic(["RDDM0001"]);
let SensitiveDataExportViaCR= SAPCRLog
| where SystemID in (SelectedSystems)
| where isnotempty( SystemID)
| where isnotempty(TableName)
| lookup SensitiveTables on TableName
| where Status == "R" // released
| where Request startswith_cs SystemID
| project-rename ExportDT= UpdatedOn
| summarize Requests= make_set(Request,10), ExportDT= min(ExportDT) by Owner, RequestDescription= Description, TableName, SystemID, Instance, Host, IsSensitive;
let TransportReleaseTransactions= SAPAuditLog
| where  MessageID == "AU3"
| where SystemID in (SelectedSystems)
| where TransactionCode in(TransportReleaseTCODEs) or ABAPProgramName in (TransportReleasePrograms)
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| project-rename TransactionStartDT= UpdatedOn_t
| summarize TransactionCodes= make_set(TransactionCode, 3), ABAPProgramNames= make_set(ABAPProgramName, 3), Email= any(Email), TransactionStartDT= min(TransactionStartDT) by User, TerminalIPv6, Host, SystemID, ClientID, Instance;
TransportReleaseTransactions
| join kind= inner SensitiveDataExportViaCR on SystemID
| extend TimePassed= datetime_diff( "minute", ExportDT, TransactionStartDT)
| where TimePassed between (0 .. 60)
| extend AlertDescription= strcat("User has exported ",iff(IsSensitive, "sensitive", "tabular") ," data from a productive system using a transport")
| extend ExtendedDetails= pack("TransactionStartDT" , TransactionStartDT, "Owner", Owner, "RequestDescription", RequestDescription, 'TransactionCodes', TransactionCodes, 'ABAPProgramNames', ABAPProgramNames)
| extend Severity= iff(IsSensitive, "High", "Medium"), Dummy= ''
| project ExportedOn= ExportDT, User, TerminalIPv6, Host, SystemID, TransactionCode= TransactionCodes[0], ABAPProgramName= ABAPProgramNames[0], Email, ExtendedDetails, Requests, AlertDescription, TableName, IsSensitive, Severity, ClientID, Instance, Dummy

// SAP - (Preview) Dynamic Anomaly based Audit Log Monitor Alerts
let SelectedSystems =  dynamic(["All Systems"]);//can also do// let SelectedSystems =  dynamic(["S4P", "B4X", "ODT"]);
let SelectedSystemRoles =  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]);
let SelectedSeverities=  dynamic(["High", "Medium"]);//can also do//let SelectedSeverities =  dynamic(["All Severities"])
// get special users for exclusion and in-focus analysis
let SAPUsersGottenVIP = (SAPUsersGetVIP | project-rename User = SAPUser);
// let SelectedRuleTypes = dynamic(["All RuleTypes"]);
let SelectedRuleTypes= dynamic(["AnomaliesOnly"]);
let SAPAuditAnomal = SAPAuditLogAnomalies(LearningTime = 14d, DetectingTime=1h, SelectedSystems= SelectedSystems,
SelectedSystemRoles= SelectedSystemRoles, SelectedSeverities= SelectedSeverities, SelectedRuleTypes= SelectedRuleTypes);
// Query
// get the anomalies
SAPAuditAnomal
// get the user privleged status
| lookup (SAPUsersGetPrivileged() | extend IsPrivilegedUser = true) on
$left.SystemID == $right.SystemID
,$left.User == $right.User
,$left.ClientID == $right.Client
| extend DetailedDescription = iff(IsPrivilegedUser, strcat('User ', User,  ' is a privileged user! <br/>', DetailedDescription), DetailedDescription)
| extend TimeGenerated = MaxTime
// handle threshold per system (configurable via the SAPAuditLogConfigurator watchlist)
//  apply the threshold criteria, consider that the watchlist describes hourly thresholds
| extend MinutesSpan = datetime_diff('Minute', MaxTime, MinTime )
| where EventCount > (Threshold * max_of(MinutesSpan, 10) / 60)
| extend DetailedDescription = iff(EventCount > 1, strcat(' Event occured ', EventCount, ' times within ', MinutesSpan + 1, ' minutes.<br/> ', 'The predefined threshold of ', Threshold,  ' Events per hour was exceeded! <br/>', DetailedDescription), DetailedDescription)
| extend MessageText = iff(MessageText endswith_cs ".", substring(MessageText, 0, strlen(MessageText)-1), MessageText)
| project-keep SystemID, Instance, MessageText, MessageClass, MessageID, ClientID, User, TerminalIPv6, TransactionCode, ABAPProgramName, SAPProcesType, SAPWPName, Email, Host, CategoryName, DestinationEmail, DetailedDescription, Tactics, TeamsChannelID, SystemRole, SystemUsage, IsProd, Severity, BagOfDetails, IsPrivilegedUser, EventCount, Threshold, TimeGenerated
| extend Source= 'SAPAuditAnom1 '

let TimeAgo = 65m;
let ioc_lookBack = 14d;
// CONFIGURATION OPTION- determine which system roles are relevant for this alert
let SelectedSystemRoles=  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
// let SelectedSystemRoles= dynamic(["All System Roles"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles) | project SystemID= SearchKey;
let MaliciousIPs= SAPThreatIntelligenceIndicator
| where TimeGenerated >= ago(ioc_lookBack) and ExpirationDateTime > now()
| summarize LatestIndicatorTime = arg_max(TimeGenerated, *) by IndicatorId
| summarize ConfidenceScore= any(ConfidenceScore) by NetworkIP, EmailSourceIpAddress, NetworkDestinationIP, NetworkSourceIP, Active, Description, ThreatType
| where Active == true
// Picking up only IOC's that contain the entities we want
| where isnotempty(NetworkIP)
    or isnotempty(EmailSourceIpAddress)
    or isnotempty(NetworkDestinationIP)
    or isnotempty(NetworkSourceIP)
// As there is potentially more than 1 indicator type for matching IP, taking NetworkIP first, then others if that is empty.
// Taking the first non-empty value based on potential IOC match availability
| extend TI_ipEntity = iff(isnotempty(NetworkIP), NetworkIP, NetworkDestinationIP)
| extend TI_ipEntity = iff(isempty(TI_ipEntity) and isnotempty(NetworkSourceIP), NetworkSourceIP, TI_ipEntity)
| extend TI_ipEntity = iff(isempty(TI_ipEntity) and isnotempty(EmailSourceIpAddress), EmailSourceIpAddress, TI_ipEntity)
//Exclude local addresses, using the ipv4_is_private operator
| where TI_ipEntity !startswith "fe80"
    and TI_ipEntity !startswith "::"
    and TI_ipEntity !startswith "127."
| summarize by TI_ipEntity, TIDescription= Description, ThreatType, ConfidenceScore
| extend IsPrivate= ipv4_is_private(TI_ipEntity);
// Get all downloads
let FileDownloads= SAPAuditLog
    | where MessageID == "AUY"
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where SystemID in (SelectedSystems)
    | join kind=inner (MaliciousIPs) on $left.TerminalIPv6 == $right.TI_ipEntity
    | extend ByteCount= toint(replace_string(replace_string(Variable1, ".",""), ",","")), Code=Variable2, Path= Variable3
    | summarize ConfidenceScore= any(ConfidenceScore), IsPrivate=any(IsPrivate), TIDescription=any(TIDescription), ThreatType= any(ThreatType), DownloadsByUser = count(), Paths= make_set(Variable3, 10), ByteCount=sum(ByteCount) by SystemID, ClientID, User, TerminalIPv6, Email, Host, TransactionCode, Instance;
    FileDownloads
    | extend PackedDetails= pack_all()
    | extend Details= strcat("User ", User, " has downloaded ", ByteCount, " bytes data from a malicious ", iff(IsPrivate, "private","public")," IP address ")
    | extend AlertDetails= strcat("Threat Type: ", ThreatType, ", Threat Description: ", TIDescription), Dummy= ''

// SAP - High volume of potentially sensitive data exported
// high volume of file downloads by users who were obsereved accessing sensitive data
// or sensitive programs/ transactions
// !!!IMPORTANT!!!
// please make sure "Lookup data from the last" parameter is greater than a full user master data update cycle
// please keep "Lookup data from the last" to be 7 days to allow coverage of user master data required
// actual lookback is set by setting TimeAgo
let TimeAgo= 4h;
let TimeBetweenAccessAndDownload= 60m;
let ByteCountThreshold= 800000; //  Threshold in Bytes for entire lookback period
// CONFIGURATION OPTION- determine which system roles are relevant for this alert
let SelectedSystemRoles=  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
// let SelectedSystemRoles= dynamic(["All System Roles"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles) | project SystemID= SearchKey;
// Get sensitive Tables
// CONFIGURATION OPTION- list sensitive tables in the 'SAP - Sensitive Tables' watchlist
let SensitiveTables = _GetWatchlist('SAP - Sensitive Tables') | summarize by Table;
let TableAccessTcodes= dynamic(["SE16", "SE16N", "SE11", "SE16H", "SM30", "SE12", "SM31", "SE16H", "SE14", "SE54","SE17", "SE16T", "DB01", "DB02"]);
// CONFIGURATION OPTION- list sensitive transactions in the 'SAP - Sensitive Transactions' watchlist
let SensitiveTransWL= toscalar(_GetWatchlist('SAP - Sensitive Transactions') | summarize TransactionCodes= make_set(TransactionCode));
// CONFIGURATION OPTION- exclude TCODEs which are not to be considered
let ExcludedTcodes= dynamic(["S000", ""]);
let SensitiveTcodes= (set_difference(set_union(SensitiveTransWL, TableAccessTcodes), ExcludedTcodes));
// CONFIGURATION OPTION- list sensitive programs in the 'SAP - Sensitive ABAP Programs' watchlist
let SensitiveABAPReports = _GetWatchlist('SAP - Sensitive ABAP Programs');
let fixedABAPReports = datatable(ABAPProgram:string) ["RSPFLDOC"];
let UnionABAP = union isfuzzy=true SensitiveABAPReports, fixedABAPReports| summarize by ABAPProgram;
// CONFIGURATION OPTION- exclude system users which are OK to download massive amounts of sensitive data
// by adding those users in the SAP_User_Config watchlist with a tag of 'MassiveDownloadsOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['MassiveDownloadsOK','SAP_ROLE1', 'SAP_PROFILE1']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
// Get direct sensitive table access
let SensitiveTableAccess= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "DU9"
    | where SystemID in (SelectedSystems)
    | where Variable1 in (SensitiveTables)
    | project-rename Table = Variable1, Activity = Variable2
    | summarize Activities= make_set(Activity, 3) by TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, ABAPProgramName
    , Table, SourceAction= strcat("after visiting transaction ", TransactionCode, " and viewing sensitive table ", Table), TransactionCode, TableOrProgram= Table;
// get sensitive program execution
let SensitiveProgramExec= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "AUW"
    | where SystemID in (SelectedSystems)
    | where ABAPProgramName in (UnionABAP)
    | summarize by TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, ABAPProgramName
    , SourceAction= strcat("after executing sensitive program ", ABAPProgramName), TransactionCode, TableOrProgram= ABAPProgramName ;
// get sensitive transaction execution
let SensitiveTcodeExec= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "AU3"
    | where SystemID in (SelectedSystems)
    // make sure sensitive transactions do not contain the TableAccessTcodes which are checked for the specific table name
    | where TransactionCode in (set_difference(SensitiveTcodes,TableAccessTcodes))
    | summarize by TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, ABAPProgramName, SourceAction= strcat("after executing sensitive transaction ", TransactionCode), TransactionCode, TableOrProgram= TransactionCode;
// Get all downloads
let FileDownloads= SAPAuditLog
    | where MessageID == "AUY"
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where SystemID in (SelectedSystems)
    | where TransactionCode in (SensitiveTcodes) or ABAPProgramName in (UnionABAP)
    | join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude // exclude specific users
    | extend ByteCount= toint(replace_string(replace_string(Variable1, ".",""), ",","")), Code=Variable2, Path= Variable3
    | summarize DownloadsByUser = count(), Paths= make_set(Variable3, 10), ByteCount=sum(ByteCount) by TimeDownloaded= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, TerminalIPv6, Email, Host, TransactionCode, Instance;
// Time window join to find dowloads after table access
FileDownloads | join kind=inner (SensitiveTableAccess | union SensitiveProgramExec, SensitiveTcodeExec) on User, SystemID
    | where (TimeAccessed- TimeDownloaded) between (-(TimeBetweenAccessAndDownload*2) .. 0h)
    | summarize Activities= tostring( make_set_if(Activities, tostring(Activities) != "")), DownloadsByUser= max(DownloadsByUser), ByteCount=max(ByteCount), TablesOrPrograms=make_set(TableOrProgram,10), SourceActions= make_set(SourceAction,10), TimeAccessed= any(TimeAccessed) by TimeDownloaded, User, SystemID, ClientID, Email, Host , TerminalIPv6, Paths= tostring(Paths), Instance
    | where ByteCount > ByteCountThreshold
    | extend PackedDetails= pack_all()
    | extend Details= strcat("User ", User, " has downloaded ", ByteCount, " bytes of potentially sensitive data ")
    | extend AlertDetails= strcat(Details , tostring(SourceActions)), Dummy= ''

// SAP - Potentially sensitive data exported via spool request
// Spool requests made by users who were obsereved accessing sensitive data
// or sensitive programs/ transactions
// actual lookback is set by setting TimeAgo
let TimeAgo= 4h;
let TimeBetweenAccessAndDownload= 30m;
// CONFIGURATION OPTION- determine which system roles are relevant for this alert
let SelectedSystemRoles=  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
// let SelectedSystemRoles= dynamic(["All System Roles"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles)
    | project SystemID= SearchKey;
// Get sensitive Tables
// CONFIGURATION OPTION- list sensitive tables in the 'SAP - Sensitive Tables' watchlist
let SensitiveTables = _GetWatchlist('SAP - Sensitive Tables')
    | summarize by Table;
let TableAccessTcodes= dynamic(["SE16", "SE16N", "SE11", "SE16H", "SM30", "SE12", "SM31", "SE16H", "SE14", "SE54", "SE17", "SE16T", "DB01", "DB02"]);
// CONFIGURATION OPTION- list sensitive transactions in the 'SAP - Sensitive Transactions' watchlist
let SensitiveTransWL= toscalar(_GetWatchlist('SAP - Sensitive Transactions')
    | summarize TransactionCodes= make_set(TransactionCode));
// CONFIGURATION OPTION- exclude TCODEs which are not to be considered
let ExcludedTcodes= dynamic(["S000", ""]);
let SensitiveTcodes= (set_difference(set_union(SensitiveTransWL, TableAccessTcodes), ExcludedTcodes));
// CONFIGURATION OPTION- list sensitive programs in the 'SAP - Sensitive ABAP Programs' watchlist
let SensitiveABAPReports = _GetWatchlist('SAP - Sensitive ABAP Programs');
let fixedABAPReports = datatable(ABAPProgram: string) ["RSPFLDOC"];
let UnionABAP = union isfuzzy=true SensitiveABAPReports, fixedABAPReports
    | summarize by ABAPProgram;
// CONFIGURATION OPTION- exclude system users which are OK to print sensitive data
// by adding those users in the SAP_User_Config watchlist with a tag of 'PrintSensitivesOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['PrintSensitivesOK', 'SAP_ROLE1', 'SAP_PROFILE1']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)
    | summarize by User2Exclude=SAPUser;
// Get direct sensitive table access
let SensitiveTableAccess= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo * 2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "DU9"
    | where SystemID in (SelectedSystems)
    | where Variable1 in (SensitiveTables)
    | project-rename Table = Variable1, Activity = Variable2
    | summarize Activities= make_set(Activity, 3)
        by TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, ABAPProgramName, Email, Host, TerminalIPv6
        , Table, SourceAction= strcat("after visiting transaction ", TransactionCode, " and viewing sensitive table ", Table), TransactionCode, TableOrProgram= Table, Instance;
// get sensitive program execution
let SensitiveProgramExec= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo * 2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "AUW"
    | where SystemID in (SelectedSystems)
    | where ABAPProgramName in (UnionABAP)
    | summarize
        by
        TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload),
        SystemID,
        ClientID,
        User,
        ABAPProgramName,
        Email,
        Host,
        TerminalIPv6,
        SourceAction= strcat("after executing sensitive program ", ABAPProgramName),
        TransactionCode,
        TableOrProgram= ABAPProgramName,
        Instance;
// get sensitive transaction execution
let SensitiveTcodeExec= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo * 2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "AU3"
    | where SystemID in (SelectedSystems)
    // make sure sensitive transactions do not contain the TableAccessTcodes which are checked for the specific table name
    | where TransactionCode in (set_difference(SensitiveTcodes, TableAccessTcodes))
    | summarize
        by
        TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload),
        SystemID,
        ClientID,
        User,
        ABAPProgramName,
        SourceAction= strcat("after executing sensitive transaction ", TransactionCode),
        TransactionCode,
        TableOrProgram= TransactionCode,
        Email,
        Host,
        TerminalIPv6,
        Instance;
// Get all spool requests
let SpoolRequests= SAPSpoolLog
    | where TimeGenerated > ago (TimeAgo * 2) and ingestion_time() > ago(TimeAgo)
    | summarize
        by
        SpoolRequestNumber,
        ClientID,
        User,
        RecipientofSpoolRequest,
        SpoolRequestSuffix2,
        SpoolRequestTitle,
        SystemID,
        OutputDevice,
        DeleteSpoolRequestAuto,
        TimeRequested= TimeGenerated,
        ValueAuthCheck;
let SpoolOutputs= SAPSpoolOutputLog
    | where TimeGenerated > ago (TimeAgo * 2) and ingestion_time() > ago(TimeAgo)
    | summarize
        by
        SpoolRequestNumber,
        User,
        RecipientofSpoolRequest,
        PhysicalFormatType,
        SystemID,
        ClientID,
        TimePrinted= TimeGenerated
    | lookup (SAP_USR02
        | summarize by SystemID, RecipientofSpoolRequest= BNAME, USTYP, ClientID= MANDT)
        on RecipientofSpoolRequest, SystemID, ClientID
    | extend
        HasPrinted= true,
        PrintRecievedByAnother= iff(User == RecipientofSpoolRequest, false, iff(isempty(USTYP), false, true));
SpoolRequests
| join kind=inner (SensitiveTableAccess
    | union SensitiveProgramExec, SensitiveTcodeExec)
    on User, SystemID
| where (TimeAccessed - TimeRequested) between (-(TimeBetweenAccessAndDownload * 2) .. 0h)
| lookup SpoolOutputs on SpoolRequestNumber, SystemID, ClientID
| summarize Activities= tostring(make_set_if(Activities, tostring(Activities) != ""))
    , TablesOrPrograms=make_set(TableOrProgram, 10), SourceActions= make_set(SourceAction, 10)
    , TimeAccessed= min(TimeAccessed), TimeRequested= min(TimeRequested), HasPrinted=max(HasPrinted)
    , PrintRecievedByAnother=max(PrintRecievedByAnother), RecipientofSpoolRequest= max(RecipientofSpoolRequest)
    by
    User,
    SystemID,
    Email,
    Host,
    TerminalIPv6,
    ClientID,
    Instance
| extend PackedDetails= pack_all()
| extend Details= strcat("User ", User, " has ", iff(HasPrinted, "printed", "requested printing of"), " potentially sensitive data ")
| extend AlertDetails= strcat(Details, tostring(SourceActions), iff(PrintRecievedByAnother, strcat("<br/> Print has been pulled by another User ", RecipientofSpoolRequest), "")), Dummy= ''

// SAP - Sensitive data saved into a USB drive
// Data exported via files and saved into a recently mounted USB drive in proximity to an execution of a sensitive transaction, a sensitive program or direct access to sensitive table.
// !!!IMPORTANT!!!
// please keep "Lookup data from the last" to be 14 days to allow coverage of USB drive mount events
// actual lookback is set by setting TimeAgo
let TimeAgo= 4h;
let TimeBetweenAccessAndDownload= 60m;
let FilesDownloadedToUSB= DeviceFileEvents
    | where ingestion_time() >= ago(TimeAgo)
    | join kind=inner  (DeviceEvents
    | where ActionType == "UsbDriveMounted"
    | extend DriveLetter = tostring(AdditionalFields.DriveLetter)
    | summarize MountedDriveLetters = make_set(DriveLetter, 26) by DeviceId, DeviceName)
    on DeviceId
| extend TargetDriveLetter = tostring(split(FolderPath, "\\")[0])
| where set_has_element(MountedDriveLetters, TargetDriveLetter)
| project USBFileSize= FileSize, USBFolderPath= FolderPath;
// CONFIGURATION OPTION- determine which system roles are relevant for this alert
let SelectedSystemRoles=  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
// let SelectedSystemRoles= dynamic(["All System Roles"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles) | project SystemID= SearchKey;
// Get sensitive Tables
// CONFIGURATION OPTION- list sensitive tables in the 'SAP - Sensitive Tables' watchlist
let SensitiveTables = _GetWatchlist('SAP - Sensitive Tables') | summarize by Table;
let TableAccessTcodes= dynamic(["SE16", "SE16N", "SE11", "SE16H", "SM30", "SE12", "SM31", "SE16H", "SE14", "SE54","SE17", "SE16T", "DB01", "DB02"]);
// CONFIGURATION OPTION- list sensitive transactions in the 'SAP - Sensitive Transactions' watchlist
let SensitiveTransWL= toscalar(_GetWatchlist('SAP - Sensitive Transactions') | summarize TransactionCodes= make_set(TransactionCode));
// CONFIGURATION OPTION- exclude TCODEs which are not to be considered
let ExcludedTcodes= dynamic(["S000", ""]);
let SensitiveTcodes= (set_difference(set_union(SensitiveTransWL, TableAccessTcodes), ExcludedTcodes));
// CONFIGURATION OPTION- list sensitive programs in the 'SAP - Sensitive ABAP Programs' watchlist
let SensitiveABAPReports = _GetWatchlist('SAP - Sensitive ABAP Programs');
let fixedABAPReports = datatable(ABAPProgram:string) ["RSPFLDOC"];
let UnionABAP = union isfuzzy=true SensitiveABAPReports, fixedABAPReports| summarize by ABAPProgram;
// Get direct sensitive table access
let SensitiveTableAccess= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "DU9"
    | where SystemID in (SelectedSystems)
    | where Variable1 in (SensitiveTables)
    | project-rename Table = Variable1, Activity = Variable2
    | summarize Activities= make_set(Activity, 3) by TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, ABAPProgramName
    , Table, SourceAction= strcat("after visiting transaction ", TransactionCode, " and viewing sensitive table ", Table), TransactionCode, TableOrProgram= Table;
// get sensitive program execution
let SensitiveProgramExec= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "AUW"
    | where SystemID in (SelectedSystems)
    | where ABAPProgramName in (UnionABAP)
    | summarize by TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, ABAPProgramName
    , SourceAction= strcat("after executing sensitive program ", ABAPProgramName), TransactionCode, TableOrProgram= ABAPProgramName ;
// get sensitive transaction execution
let SensitiveTcodeExec= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "AU3"
    | where SystemID in (SelectedSystems)
    // make sure sensitive transactions do not contain the TableAccessTcodes which are checked for the specific table name
    | where TransactionCode in (set_difference(SensitiveTcodes,TableAccessTcodes))
    | summarize by TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, ABAPProgramName, SourceAction= strcat("after executing sensitive transaction ", TransactionCode), TransactionCode, TableOrProgram= TransactionCode;
// Get all downloads
let FileDownloads= SAPAuditLog
    | where MessageID == "AUY"
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where SystemID in (SelectedSystems)
    | where TransactionCode in (SensitiveTcodes) or ABAPProgramName in (UnionABAP)
    | join kind= innerunique FilesDownloadedToUSB on $left.Variable3== $right.USBFolderPath
    | extend ByteCount= toint(replace_string(replace_string(Variable1, ".",""), ",","")), Code=Variable2, Path= Variable3
    | summarize DownloadsByUser = count(), Paths= make_set(Variable3, 10), ByteCount=sum(ByteCount) by TimeDownloaded= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, TerminalIPv6, Email, Host, TransactionCode, Instance;
// Time window join to find dowloads after table access
FileDownloads | join kind=inner (SensitiveTableAccess | union SensitiveProgramExec, SensitiveTcodeExec) on User, SystemID
    | where (TimeAccessed- TimeDownloaded) between (-(TimeBetweenAccessAndDownload*2) .. 0h)
    | summarize Activities= tostring( make_set_if(Activities, tostring(Activities) != "")), DownloadsByUser= max(DownloadsByUser), ByteCount=max(ByteCount), TablesOrPrograms=make_set(TableOrProgram,10), SourceActions= make_set(SourceAction,10), TimeAccessed= any(TimeAccessed) by TimeDownloaded, User, SystemID, ClientID, Email, Host , TerminalIPv6, Paths= tostring(Paths), Instance
    | extend PackedDetails= pack_all()
    | extend Details= strcat("User ", User, " has downloaded ", ByteCount, " bytes of potentially sensitive data ")
    | extend AlertDetails= strcat(Details , tostring(SourceActions)), Dummy= ''

// Activation or Deactivation of ICF Service
// Materialize the data for self join to be esier
let TimeAgo= 2h;
let ICFDataChanges = materialize(SAPTableDataLog
| where TimeGenerated > TimeAgo
| where TableName == "ICFSERVLOC"
| where NewValue == "X" or NewValue =="" or isempty( NewValue)// X - Status is activated, "" Not activated now
| where TableField == "ICF_NAME" or TableField == "ICFACTIVE"// Service name// Active status
| project OldValue, NewValue, LogKey, DBLogID, SystemID, TimeGenerated, UserName, Host, Instance);
let ActDeactICF =
ICFDataChanges
| where OldValue == NewValue and OldValue != ""
| lookup ICFDataChanges on
LogKey, DBLogID // Lookup for combining the data
| extend ICFServiceStatus = iff(NewValue1 == "X", "Activated", iff(NewValue1 == "" or isempty( NewValue1) and OldValue1 == "X" ,"Deactivated","No Activity"))
| extend Service = split(LogKey," ")[0];
ActDeactICF
| lookup SAPUsersHeader() on SystemID, $left.UserName== $right.User
| extend AlertText= strcat("Service ", Service, " ", ICFServiceStatus, " by User ", UserName, " in System ", SystemID)//{{Service}} {{ICFServiceStatus}} by User {{User}} in System {{SystemID}}
| project TimeGenerated, Service, PrimaryIP, PrimaryEmail, KnownIPs, KnownEmails, LastKnownIP, User= UserName, Host, SystemID, Client, Instance, ICFServiceStatus, AlertText
| extend Dummy = ''

// Define Variables
// Audit Log Classes - Authorizations for user changed
// the actual lookback period is 1h. The alert looks back further to allow for user master data to be built slowly
let TimeAgo= 12h;
let Identity = 'IDENTITY';
let ProfileChangeDoc = 'SUSR_UST04';
let Insert = "I";
let logsThreshold = 3600; // 3600 seconds
let AuditClasses = dynamic(['AUB']); // Authorizations for user &A changed.
// Maintain these if WatchList is not available
let SensitiveProfiles =  _GetWatchlist('SAP - Sensitive Profiles');
let fixedProfile = datatable(Profile:string)['SAP_ALL','SAP_NEW'];// Maintain these if System doesn't have CR's
// here you can exclude system users which are OK to assign a Sensitive profile
// by adding those users in the SAP_User_Config watchlist with a tag of 'AssignSensitiveProfileOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['AssignSensitiveProfileOK']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
let fixedChangeDocs = datatable(User : string, ObjectClass : string, TableName : string, TypeofChange_Item : string , ChangedTableKey : string, ObjectID : string, TimeGenerated : datetime, ValueNew : string, SystemID : string, Instance: string, ClientID: string, UpdatedOn_t: datetime)[];
let ChangeDocs =
(union isfuzzy=true table("SAPChangeDocsLog"), fixedChangeDocs)
| where ingestion_time() > ago(TimeAgo)
| project-rename ChangedOn= UpdatedOn_t;
let IdentityChangeDocuments =
// Identity Change documents which represents profiles assignment
    ChangeDocs
    | where ObjectClass == Identity // Identity
    and TableName == ProfileChangeDoc // Profile Change Doc
    and TypeofChange_Item == Insert // Insert
    | extend Profile = ChangedTableKey
    | extend UserAssigned = ObjectID;
let UnitedProfiles =
toscalar(union fixedProfile, SensitiveProfiles
| summarize Profiles = make_list(Profile));
// Query logic
SAPAuditLog
| where ingestion_time() > ago(TimeAgo)
| where MessageID in (AuditClasses)
| project-rename AuditedOn= UpdatedOn_t
| summarize by AuditedOn, TerminalIPv6, User, Host, Email
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| lookup kind = leftouter (IdentityChangeDocuments) on User
| where Profile in (UnitedProfiles)
| where abs(datetime_diff('second',ChangedOn,AuditedOn)) <= logsThreshold
or isnull(AuditedOn)
| project
// Details
ChangedOn, Instance , SystemID, ClientID, Profile, User,  UserAssigned,
Email, TerminalIPv6, Host, AuditedOn
| extend Dummy= '',  PackedDetails= pack_all()

// Assignment of a sensitive role
// Define Variables
let Roles = 'PFCG';
let UsersRoles = 'AGR_USERS';
let Insert = "I";
let logsThreshold = 3600; // 3600 seconds
// Audit Log Classes - Authorizations for user changed
let AuditClasses = dynamic(['AUB', 'AUD']); // Authorizations for user &A changed.
// Maintain these if WatchList is not available
let SensitiveRoles =  _GetWatchlist('SAP - Sensitive Roles');
let fixedRoles = datatable(Role: string)['SAP_BC_BASIS_ADMIN', 'SAP_BC_AUTH_PROFILE_ADMIN'];
// here you can exclude system users which are OK to assign sensitive roles
// by adding those users in the SAP_User_Config watchlist with a tag of 'SensitiveRoleOK'
let excludeUsersTags= dynamic(['SensitiveRoleOK']);
let excludedUsers= _GetWatchlist('SAP_User_Config')| where Tags has_any (excludeUsersTags)| summarize by User2Exclude=SAPUser;
let UnitedRoles =
toscalar(union fixedRoles, SensitiveRoles
| summarize Roles = make_list(Role));
let ChangeCheck =
SAPChangeDocsLog
| where ObjectClass == Roles // Roles
    and TableName == UsersRoles // Users Roles
    and TypeofChange_Item == Insert // Insert
| extend UserAssigned = trim_end(@"[^\w]+" ,extract(@"^.{1,33}\s*?(.{1,12})\s*?\d{16}", 1, ChangedTableKey))
| extend Role = ObjectID
| where Role in (UnitedRoles);
// Query logic
SAPAuditLog
| where MessageID in (AuditClasses)
| summarize by TimeGenerated, TerminalIPv6, User, Host, Email, ClientID, SystemID
| extend User= trim_end(@"[^\w]+" , User)
| lookup kind = inner (ChangeCheck) on User, ClientID, SystemID
| where Role in (UnitedRoles)
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| project-rename TimeGenAudit = TimeGenerated1
| where abs(datetime_diff('second', TimeGenerated, TimeGenAudit)) <= logsThreshold
or isnull(TimeGenAudit)
| extend AlertDescription= iff(User == UserAssigned,strcat('User ', User, ' has self-assigned a sensitive role '),strcat('User ', User, ' has Assigned a sensitive role to User ', UserAssigned))
| extend Severity= iff(User == UserAssigned, "High", "Medium")
| extend PackedDetails= pack_all(), Dummy=' '
| lookup (SAPUsersEmail | project SystemID, ClientID, UserAssigned= User, UserAssignedEmail= Email) on UserAssigned, SystemID, ClientID
| extend Tactics= "Privilege Escalation"
| project
    // Details
    TimeGenerated, Instance , SystemID, ClientID, Role, User, UserAssigned,
    Email, TerminalIPv6, Host, AlertDescription, Severity, PackedDetails, UserAssignedEmail, Tactics

// Define variables
// Audit Log Classes - Failed Logons / Password Check
// let AuditClasses = dynamic(['AUO', 'AU2', 'AU6', 'BU1']);
let AuditClasses = dynamic(['AU6']);//RFC only
let perIPLimit = 300;
// Query logic
SAPAuditLog
| where MessageID in (AuditClasses)
| extend DetailsBy = pack("User", User, "Email", Email, "SystemID", SystemID, "ClientID", ClientID)
| summarize LoginbyIPAttempts = count(), Details = make_set(DetailsBy), StartTime = min(TimeGenerated), EndTime = max(TimeGenerated)
    by TerminalIPv6
// Check if number of login attempts per IP is higher than limit
| where LoginbyIPAttempts > perIPLimit
| mv-expand Details
| evaluate bag_unpack(Details, "Details_")
| project
    StartTime, EndTime,  TerminalIPv6,
    Email = column_ifexists("Details_Email", ""), User = column_ifexists("Details_User", ""),
    SysetmID = column_ifexists("Details_SystemID", ""),
   ClientID = column_ifexists("Details_ClientID", "")

// Change in a Sensitive Privileged User
// time span for audit events
let AuditTimeAgo= 75m;
// Audit Log Classes - User Master Changes
let AuditClasses = dynamic(['AU7', 'BUV', 'BUW', 'AU8', 'AU9', 'AUA', 'AUB', 'AUD', 'DUH', 'BU2']);
let UsersEmails = SAPUsersHeader()
| project-keep User, SystemID, Client, Email
| project-rename ClientID= Client, ChangedEmail=Email, ChangedUser=User;
SAPAuditLog
| where TimeGenerated > ago(AuditTimeAgo)
| where MessageID in (AuditClasses)
// The user that we are making change in is a sensitive privileged user
| extend ChangedUser = Variable1
| join kind= inner (SAPUsersGetPrivileged())
on $left.ClientID == $right.Client
, $left.SystemID == $right.SystemID
, $left.ChangedUser == $right.User
| extend MessageText = iff(MessageText endswith_cs ".", substring(MessageText, 0, strlen(MessageText)-1), MessageText)
| extend MessageTextDyna = replace_string(MessageText, "User", "Privileged User")
| extend MessageTextDyna = replace_string(MessageTextDyna, "user", "Privileged User")
| lookup UsersEmails
on SystemID, ClientID, ChangedUser
| project-keep TimeGenerated, Instance,  SystemID, ClientID, User, MessageText, MessageID, Email,  TerminalIPv6, Host, ChangedUser, ChangedEmail, MessageTextDyna
| extend Email= iff(isempty(Email),User, Email), Host= iff(isempty(Host), TerminalIPv6, Host), Dummy= ''
| extend ChangedEmail= iff(isempty(ChangedEmail),ChangedUser, ChangedEmail)
| extend PackedDetails= pack_all()

// New Assigned Users
let ObjectClassRoles = 'PFCG';
let TableName = 'CD1251';
let UsersRoles = 'AGR_USERS';
let Insert = "I";
let NotInUse = 'NOT_IN_USE';
let logsThreshold = 3600; // 3600 seconds
// Audit Log Classes - Authorizations for user changed
let AuditClasses = dynamic(['AUB','AUD']); // Authorizations for user &A changed. User Master Record Changed
// Roles Change Documents - Extract Auth Object and Obj Field
let allHistory = ago(0d);
let alertSched = ago(6h); // Please maintain according to schedule
// Maintain these if System doesn't have CR's
let fixedChangeDocs = datatable(User : string, ObjectClass : string, TableName : string, TypeofChange_Item : string , ChangedTableKey : string, ObjectID : string, TimeGenerated : datetime, ValueNew : string, SystemID : string)[];
let ChangeDocs =
union isfuzzy=true table("SAPChangeDocsLog"), fixedChangeDocs;
let RolesAuthObject = ChangeDocs
    | where TimeGenerated <= allHistory
    | where ObjectClass == ObjectClassRoles and TableName == TableName // Role-Obj-Profile-ObjField
    | where TypeofChange_Item in ('J', 'I', 'U') //  Insert
    | extend RoleObjProfileObjFieldVer = ChangedTableKey, Role = ObjectID
    | extend ObjFieldValue = ValueNew
    | extend ObjField = trim(@"\s*?", extract(@"(^.{1,30})\s*?(.{1,10})\s*?(.{1,12})\s*?(.{1,10})\s*?\d{6}", 4, RoleObjProfileObjFieldVer, typeof(string)))
    | extend AuthObject = trim(@"\s*?", extract(@"(^.{1,30})\s*?(.{1,10})\s*?(.{1,12})\s*?(.{1,10})\s*?\d{6}", 2, RoleObjProfileObjFieldVer, typeof(string)))
    | summarize by SystemID, Role, AuthObject, ObjField, ObjFieldValue;
let ComplexAuth = _GetWatchlist('SAP - Critical Authorizations');
let SimpleAuth = _GetWatchlist('SAP - Critical Authorizations');
let fixedComplexAuth = datatable(AuthorizationObject: string, AuthorizationField: string, AuthorizationValue: string, ActivityField: string, Activity: string)
    ['S_DEVELOP', 'OBJTYPE', 'DEBUG', 'ACTVT', '*',
    'S_DEVELOP', 'OBJTYPE', 'DEBUG', 'ACTVT', '02']; // Maintain these if WatchList is not available
let fixedSimpleAuth = datatable(AuthorizationObject: string, AuthorizationField: string, AuthorizationValue: string, ActivityField: string, Activity: string)
    ['S_TCODE', 'TCD', '*', 'NOT_IN_USE', '',
    'S_TZONE', 'ACTVT', '*', 'NOT_IN_USE', '']; // Maintain these if WatchList is not available
let usersinRole =
    ChangeDocs
    | where TimeGenerated >= alertSched
    | where ObjectClass == ObjectClassRoles // Roles
        and TableName == UsersRoles // Users Roles
        and TypeofChange_Item == Insert // Insert
    | extend UserAssigned = extract(@"^.{1,33}\s*?(.{1,12})\s*?\d{16}", 1, ChangedTableKey)
    | extend Role = ObjectID
    | extend TimeGenUserinRole = TimeGenerated;
    //| summarize by TimeGenerated, SystemID, ClientID, Role, UserAssigned, User
let RolesAuthObjectCheck =
        RolesAuthObject
        | extend ObjFieldVal = ObjFieldValue
        | lookup kind = leftouter
            (RolesAuthObject
            | extend ActivityVal = ObjFieldValue)
            on Role, AuthObject;
let complexScenario =
    union ComplexAuth, fixedComplexAuth
    | where ActivityField != NotInUse
    | summarize by AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity
    | lookup kind = inner (RolesAuthObjectCheck)
        on $left.AuthorizationObject == $right.AuthObject
        and $left.AuthorizationField == $right.ObjField
        and $left.AuthorizationValue == $right.ObjFieldValue
        and $left.ActivityField == $right.ObjField1
        and $left.Activity == $right.ActivityVal;
let simpleScenario =
    union SimpleAuth, fixedSimpleAuth
    | where ActivityField == NotInUse
    | summarize by AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity
    | lookup kind = inner (RolesAuthObject)
        on $left.AuthorizationObject == $right.AuthObject
        and $left.AuthorizationField == $right.ObjField
        and $left.AuthorizationValue == $right.ObjFieldValue;
let GetEntites =
    SAPAuditLog
    | where TimeGenerated >= alertSched
    | where MessageID in (AuditClasses)
    | summarize by TimeGenerated, TerminalIPv6, ClientID, User, Host, Email
    | extend TimeGenAudit = TimeGenerated;
union complexScenario, simpleScenario
| lookup kind = inner (usersinRole) on SystemID, Role
| lookup kind = leftouter (GetEntites) on User
| where abs(datetime_diff('second', TimeGenUserinRole, TimeGenAudit)) <= logsThreshold or
isnull(TimeGenAudit)
| project
    // Details
TimeGenUserinRole , SystemID, ClientID, Role, User, UserAssigned, AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity, Email, TerminalIPv6, Host

let Role = 'Production';
let AuditClasses = dynamic(['CUL']); // Audit Log Classes - Debug Change
let allSystemRoles = dynamic(['Sandbox','Developement','QualityAssurance','Training','Production']); // Available System Roles
let allSystemUsage = dynamic (['ERP','CRM','BW','Solman','Gateway','Enterprise Portal']); // Available System Usages
// Get Relevant Systems from WatchList
let systemID = _GetWatchlist('SAP - Systems');
let fixedSID = datatable(SystemID:string, SystemRole:string, SystemUsage:string)
// Maintain these if WatchList is not available
    ["S4H","Production","ERP",
    "XXX","Sandbox","BW"]
;
let UnitedSystem =
union systemID, fixedSID
