// this function returns privilileged and sensitive users
// privileged users are users which are directly named as such 
let PrivelegedUsers = _GetWatchlist('SAP - Privileged Users');
let FixedUsers = datatable(User: string, Client: string, SystemID: string) ["DDIC", "XXX","001"];
// privileged users can also be such that carry a privileged role
let fixedRoles = datatable(Role: string) ['SAP_BC_BASIS_ADMIN', 'SAP_BC_AUTH_PROFILE_ADMIN'];
// privileged users can also be such that carry a privileged profile
let fixedProfiles = datatable(Profile: string) ["SAP_ALL", "SAP_NEW"];
// sensitive users are such that hold a sensitive role:
let SensRoles = _GetWatchlist('SAP - Sensitive Roles') | summarize by SearchKey;
// or a sensitive profile:
let SensProfiles = _GetWatchlist('SAP - Sensitive Profiles') | summarize by SearchKey;
// get SAP systems which are relevant
let AuditTimeAgo = totimespan(toscalar(SAPGetParameters('UserMDLookBack')| project-keep ValueLow));
let availableSystems= SAPAuditLog
| where TimeGenerated > ago(AuditTimeAgo)
| summarize by SystemID, Client= ClientID
| extend dummy ='';
// get the user assignments to roles and profiles
let UserAssignments= materialize(SAPUsersAssignments() | project User, ChildRoles, DirectRoles, Profiles, Client, SystemID);
// privileged users
let PrivUsersByMD = UserAssignments
| mv-expand ChildRoles, DirectRoles, Profiles
| where DirectRoles in (fixedRoles) or ChildRoles in (fixedRoles) or Profiles in (fixedProfiles)
| summarize by User, Client, SystemID
| extend IsPrivileged= true;
// sensitive users
let SensitiveUsersByMD = UserAssignments
| project User, ChildRoles, DirectRoles, Profiles, Client, SystemID
| mv-expand ChildRoles, DirectRoles, Profiles
| where DirectRoles in (SensRoles) or ChildRoles in (SensRoles) or Profiles in (SensProfiles)
| summarize by User, Client, SystemID
| extend IsPrivileged= false;
// cross join watchlist users with available systems and clients
let FixedUPriv = ((PrivelegedUsers
    | union isfuzzy=true  FixedUsers 
    | summarize by User
    | extend dummy = '') 
    | join kind= fullouter availableSystems
        on $left.dummy == $right.dummy)
    | extend IsPrivileged= true;
FixedUPriv
| union isfuzzy=true PrivUsersByMD, SensitiveUsersByMD, FixedUsers
| where Client != "XXX"
| summarize IsPrivileged= max(IsPrivileged) by User, Client, SystemID

// function gets tags which can either be SAP roles, SAP Profiles or tags.
// let SearchForTags= dynamic(['Sensitive', 'T-AH390004']);
// let SearchForTags= todynamic('All Tags');
// let SpecialFocusTags = split('SpecialAttention; SoonToLeave', ';');
// get the SAP roles / SAP profiles that are relevant
let SAPUserRolesAndProfiles = SAPUsersAssignments 
| where (tostring(SearchForTags) == 'All Tags' or Profiles has_any (SearchForTags) or ChildRoles has_any (SearchForTags) or DirectRoles has_any (SearchForTags))
| project-keep User, SystemID, DirectRoles, ChildRoles, Profiles 
| summarize RolesAndProfiles = make_set(array_concat(DirectRoles, ChildRoles, Profiles), 120) by SAPUser= User
| extend SearchKey= SAPUser;
// query
_GetWatchlist('SAP_User_Config')
// remove spaces
| extend Tags = replace_string(Tags, ' ', '')
| extend TagsArr= split(Tags,";")
| summarize Tags=make_set(Tags, 30), TagsList= make_set(TagsArr, 200), arg_max(LastUpdatedTimeUTC, SAPUser, ['User AAD Object Id'],	['User Identifier'],	['User On-Prem Sid'],	['User Principal Name']) by SearchKey
| join kind= inner SAPUserRolesAndProfiles on SAPUser
| extend TagsList = array_concat(TagsList, RolesAndProfiles)
| extend TagsIntersect = set_intersect(TagsList, SearchForTags)
| extend SpecialFocusTagged = iff(array_length(set_intersect(TagsList, SpecialFocusTags)) > 0, true, false)
| extend IntersectionSize = array_length(TagsIntersect)
| extend Tags= replace_string(tostring(Tags),',','; ')
| extend Tags= translate('[]"','', Tags)
| where (IntersectionSize > 0 or tostring(SearchForTags) == 'All Tags' or SpecialFocusTagged == true)
| project-away LastUpdatedTimeUTC, SAPUser1, SearchKey1

// // optional parameters can look like this
// let SelectedSystems =  dynamic(["All Systems"]);
// let SelectedSystemRoles =  dynamic(["All System Roles"]);
// let SelectedUsers = dynamic(["All Users"]);
// let SelectedUser = "All Users";
// // let SelectedSystems =  dynamic(["S4P", "B4X", "ODT"]);
// // let SelectedSystemRoles =  dynamic(["Production", "UAT"]);
// // let SelectedUsers = dynamic(["DEVELOPER","DDIC"]); 
// // let SelectedUser = "DDIC";

let UserMDLookBack = totimespan(toscalar(SAPGetParameters('UserMDLookBack')| project-keep ValueLow));
let UserHeaderAuditLookBack = totimespan(toscalar(SAPGetParameters('UserHeaderAuditLookBack')| project-keep ValueLow));

let Now = now();
let Systems = materialize (_GetWatchlist('SAP - Systems') 
    | where SearchKey in (SelectedSystems) or tostring(SelectedSystems) == '["All Systems"]'
    | where SystemRole in (SelectedSystemRoles) or tostring(SelectedSystemRoles) == '["All System Roles"]' 
    | project-keep SystemID, SystemRole, SystemUsage);

let AuditEvents= materialize(SAPAuditLog() 
| where TimeGenerated > ago(UserHeaderAuditLookBack)
| summarize  TimeGenerated= arg_max(TimeGenerated, User, ClientID, SystemID, TerminalIPv6, Email) by User, ClientID, SystemID, TerminalIPv6, Email
| lookup kind=inner Systems on SystemID
| project-rename Client= ClientID);

let LastKnownIPs = AuditEvents | where isnotempty(TerminalIPv6)| summarize LastUsedOn= arg_max(TimeGenerated, *) by User, Client, SystemID | project LastKnownIP= TerminalIPv6, User, Client, SystemID;

let KnownIPsAndEmails = AuditEvents
| summarize Count= count() by User, Client, SystemID, TerminalIPv6, Email| order by Count desc
| summarize KnownEmails= make_set_if(Email,isnotempty( Email), 10), KnownIPs = make_set_if(TerminalIPv6, isnotempty(TerminalIPv6), 10) 
by User, SystemID, Client
| extend PrimaryEmail = KnownEmails[0], PrimaryIP= KnownIPs[0];

// query
Systems | join kind= inner 
// get logon Data (Kernel-Side Use)
(SAP_USR02 | where TimeGenerated > ago(UserMDLookBack) 
| where (BNAME in (SelectedUsers) or tostring( SelectedUsers) == '["All Users"]') and (BNAME == SelectedUser or SelectedUser == "All Users")
| summarize arg_max(TimeGenerated, *) by BNAME, MANDT, SystemID )
on SystemID

// get User Name/Address Key Assignment
| join kind=leftouter hint.strategy=shuffle 
(SAP_USR21 | where TimeGenerated > ago(UserMDLookBack) | summarize arg_max(TimeGenerated, *) by BNAME, MANDT, SystemID)
on BNAME, MANDT, SystemID
| project-rename CLIENT= MANDT

// get E-Mail Addresses 
| join kind= leftouter hint.strategy=shuffle 
(SAP_ADR6 | where TimeGenerated > ago(UserMDLookBack) 
| summarize arg_max(TimeGenerated, CONSNUMBER ) by CLIENT, DATE_FROM, ADDRNUMBER, PERSNUMBER, SystemID, SMTP_ADDR)
 on ADDRNUMBER, PERSNUMBER, SystemID, CLIENT
| project-rename User= BNAME, Client= CLIENT
| lookup KnownIPsAndEmails on User, SystemID, Client
| lookup LastKnownIPs on User, SystemID, Client

| extend LastSeen = todatetime(strcat(TRDAT, LTIME))
| extend LastSeenDaysAgo = datetime_diff('Day', Now, LastSeen)
| project 
// TimeGenerated,
User,
Email = SMTP_ADDR,
UserType = USTYP,
Timezone = TZONE,
LockedStatus = UFLAG,
LastSeen,
LastSeenDaysAgo,
PrimaryIP,
LastKnownIP,
PrimaryEmail,
KnownIPs,
KnownEmails,
Client,
SystemID,
SystemRole,
SystemUsage

////// get anomalies from the SAPAuditLog
//// Dynamic parameters
// let LearningTime = 14d;
// let DetectingTime = 1h;// use 1 h for alerting on anomalies // can also do // let DetectingTime = 0h;//0h for seeing anomalies across all training data
// let SelectedSystems =  dynamic(["All Systems"]);//can also do// let SelectedSystems =  dynamic(["S4P", "B4X", "ODT"]);
// let SelectedSystemRoles =  dynamic(["All System Roles"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]);
// let SelectedSeverities=  dynamic(["High", "Medium"]);//can also do dynamic(["All Severities"]), dynamic(["High", "Medium", "Low"])
// let SelectedRuleTypes= dynamic(["AnomaliesOnly"]);
// let SelectedPrefixMask = 24;
// // other potential values can be: 
// // let SelectedRuleTypes= dynamic(["Deterministic"]);
// // let SelectedRuleTypes = dynamic(["All RuleTypes"]);
//// Static parameters
// If DetectingTime==0h, we want anomalies to show across all training time as well
// 1h hour detecting will spill over 2 hour bins
let ActualDetectingTime= iff(DetectingTime == 0h, LearningTime, DetectingTime + 1h);
let TestPoints= iff(DetectingTime == 0h, 0, toint((DetectingTime + 1h) / 1h));
let Now = now();
let AnomalyThreshold= 2; 
let AnomalyTrendKind= 'linefit';// can be 'linefit', 'avg' or 'none'
let AnomalySeasonality= 24; // can be 0, or number of bins, -1 is AutoDetect
let AnomalyAD_method = 'ctukey'; // 'ctukey' = 10th-90th percentile, 'tukey' = 25th-75th percentile range 
// get special users for exclusion and in-focus analysis
let SAPUsersGottenVIP = materialize(SAPUsersGetVIP
    | project-rename User = SAPUser);
let SAPUserRolesAndProfiles = (SAPUsersAssignments
    | project-keep User, SystemID, DirectRoles, ChildRoles, Profiles 
    | summarize RolesAndProfiles = make_set(array_concat(DirectRoles, ChildRoles, Profiles), 120) by User, SystemID); 
// get the configuration from the "SAPAuditLogConfiguration" watchlist which allows
// assigning severity levels per system Role (Production/ non-production), automatic team assignments and other configuration options
let AuditConfiguration= materialize(SAPAuditLogConfiguration(SelectedSystems, SelectedSystemRoles, SelectedSeverities= SelectedSeverities, SelectedRuleTypes= SelectedRuleTypes)
    | extend FrequencyThreshold= Threshold);
// get relevant audit events according to configuration
let AuditEvents = (SAPAuditLog
        | where TimeGenerated > ago(LearningTime)
        | where SystemID in ((AuditConfiguration | summarize  by SystemID))
        | where MessageID in ((AuditConfiguration | summarize  by MessageID))
        // Alerts coming from SAP as "Low" or missing User ID aren't very intersting
        | where AlertSeverityText != "Low" and isnotempty(User)
        | join kind= leftsemi AuditConfiguration on SystemID, MessageID
        | project-away
            AuditClassID,
            AlertSeverityText,
            MonitoringObjectName,
            MonitorShortName,
            AlertValue,
            AlertSeverity,
            Variable1,
            Variable2,
            Variable3,
            Variable4,
            Type,
            SystemNumber
        | extend HourOfDay= hourofday(TimeGenerated) * 1h + startofday(TimeGenerated));
// create time-series
let TimeSeriesLog = AuditEvents
    | where TimeGenerated > ago(LearningTime)
    // aggregate by sub net
    | extend IPRange = format_ipv4(TerminalIPv6, SelectedPrefixMask)
    | make-series Count= count()
        on TimeGenerated
        from (startofday(ago(LearningTime)) + hourofday(Now) * 1h)to Now step 1h
        by 
        SystemID, MessageID, User, IPRange
        | lookup (AuditConfiguration | project SystemID, MessageID, FrequencyThreshold) on SystemID, MessageID;
// detect anomalies at aggregated level
let TimeSeriesAlerts= TimeSeriesLog
    | extend (anomalies, score, baseline) = series_decompose_anomalies(Count, Threshold= AnomalyThreshold, Seasonality= AnomalySeasonality, Trend= AnomalyTrendKind, Test_points= TestPoints)
    // expand to hourly
    | mv-expand
        Count to typeof(double),
        TimeGenerated to typeof(datetime),
        anomalies to typeof(double),
        score to typeof(double),
        baseline to typeof(long)
    | where TimeGenerated >= ago(ActualDetectingTime) 
    // apply anomaly filters + thesholds
    | where anomalies > 0
    | where score > 2
    | where Count > FrequencyThreshold
    | project-rename DiscoveredOn= TimeGenerated, AnomalCount= Count
    | project
        SystemID,
        MessageID,
        User,
        DiscoveredOn,
        score,
        baseline,
        AnomalCount,
        FrequencyThreshold
    // To reduce false positives, exclude users whos tags in the SAP_User_Config watchlist 
// match tags in the RolesTagsToExclude column in the SAP_Dynamic_Audit_Log_Monitor_Configuration
// tags can be either strings representing a group such as Batch; Sensitive; Super; MassiveLogonsOK; MassiveRFCOK; GenericTablebyRFCOK
// or SAP roles such as SAP_BC_TRANSPORT_ADMINISTRATOR, or SAP profiles like SAP_ALL
    | lookup AuditConfiguration on MessageID, SystemID
    | lookup SAPUsersGottenVIP on User
    | lookup SAPUserRolesAndProfiles on User, SystemID
    | extend RolesProfilesAndTags = array_concat(RolesAndProfiles, TagsList)
    | extend TagsIntersect = set_intersect(split(RolesTagsToExclude, ";"), RolesProfilesAndTags)
    | extend IntersectionSize = array_length(TagsIntersect)
    // keep only non-excluded users
    | where IntersectionSize == 0 or isempty(IntersectionSize)
    // fetch detailed data for anomal combinations
    | join kind= inner (AuditEvents
        | where TimeGenerated > ago(ActualDetectingTime))
        on User, SystemID, MessageID, $left.DiscoveredOn == $right.HourOfDay
| summarize EventCount= count(), AnomalCount= max(AnomalCount), MinTime= min(TimeGenerated), MaxTime = max(TimeGenerated), Score= max(score)
, take_any(Instance, MessageClass, ClientID, TerminalIPv6, TransactionCode, ABAPProgramName, SAPProcesType, SAPWPName, Email, Host, BagOfDetails, DetailedDescription, Threshold, CategoryName, DestinationEmail,  Tactics, TeamsChannelID, SystemRole, SystemUsage, IsProd, Severity, BagOfDetails, RolesTagsToExclude)
by DiscoveredOn, SystemID, MessageText, User, MessageID
| extend BagOfDetails = tostring(BagOfDetails);
TimeSeriesAlerts

//generated function structure for custom log ABAPOS_SAPCONTROL_CL 
let D_ABAPOS_SAPCONTROL_CL = datatable(TimeGenerated:datetime 
,System:string
,MessageText:string
,Type:string
)[];

let T_ABAPOS_SAPCONTROL_CL = (ABAPOS_SAPCONTROL_CL | project 
TimeGenerated = TimeGenerated
,System = System_s
,MessageText = MessageText_s
,Type = Type
);

T_ABAPOS_SAPCONTROL_CL | union isfuzzy= true D_ABAPOS_SAPCONTROL_CL

// this function returns privilileged user by default but can also return sensitive users
// let IncludeSensitiveUsers= false;// can also do IncludeSensitive= true which will include users with sensitive profiles
// privileged users are users which are directly named as such 
let PrivelegedUsers = _GetWatchlist('SAP - Privileged Users');
let FixedUsers = datatable(User: string, Client: string, SystemID: string) ["DDIC", "XXX","001"];
// privileged users can also be such that carry a privileged role
let fixedRoles = datatable(Role: string) ['SAP_BC_BASIS_ADMIN', 'SAP_BC_AUTH_PROFILE_ADMIN'];
// privileged users can also be such that carry a privileged profile
let fixedProfiles = datatable(Profile: string) ["SAP_ALL", "SAP_NEW"];
let AuditTimeAgo = totimespan(toscalar(SAPGetParameters('UserMDLookBack')| project-keep ValueLow));
let availableSystems= SAPAuditLog
| where TimeGenerated > ago(AuditTimeAgo)
| summarize by SystemID, Client= ClientID
| extend dummy ='';
// get the user assignments to roles and profiles
let UserAssignments= materialize(SAPUsersAssignments() | project User, ChildRoles, DirectRoles, Profiles, Client, SystemID);
// privileged users
let PrivUsersByMD = UserAssignments
| mv-expand ChildRoles, DirectRoles, Profiles
| where DirectRoles in (fixedRoles) or ChildRoles in (fixedRoles) or Profiles in (fixedProfiles)
| summarize by User, Client, SystemID
| extend IsPrivileged= true;

// cross join watchlist users with available systems and clients
let FixedUPriv = ((PrivelegedUsers
    | union isfuzzy=true  FixedUsers 
    | summarize by User
    | extend dummy = '') 
    | join kind= fullouter availableSystems
        on $left.dummy == $right.dummy)
    | extend IsPrivileged= true;

FixedUPriv
| union isfuzzy=true PrivUsersByMD, FixedUsers
| where Client != "XXX"
| summarize by User, Client, SystemID
