SAPAuditLog 
| where TimeGenerated {TimeSelect}
| where (SystemID in ({Systems}) or  '*' in ({Systems})) 
| where MessageID in (AuditClasses)
| where Variable1 in (DialogLogonTypes)
| where User !in (UnitedExcUsers)
| summarize CountIP = dcount(TerminalIPv6), IPs = make_set(TerminalIPv6), StartTime = min(TimeGenerated), EndTime = max(TimeGenerated) by SystemID, ClientID, User, Email
| where CountIP > IPThreshold // Count of IP logins from the user is higher than threshold
| mv-expand IPs to typeof(string ) // Show for each IP
| project SystemID, ClientID, User,
    AccountCustomEntity = Email, IPCustomEntity = IPs, StartTime, EndTime
