let fixedNetworks =
datatable(Network:string)
// Maintain these if WatchList is not available    
    ["111.68.128.0/1", "123.68.128.0/1"]
; 
let UnitedNetworks =
toscalar(union excNetworks, fixedNetworks
| summarize Networks = make_set(Network));
let UsersperIP = 1;
// Query logic
SAPAuditLog 
| where TimeGenerated {TimeSelect}
| where (SystemID in ({Systems}) or  '*' in ({Systems})) 
| where MessageID in (AuditClasses)
| where Variable1 in (DialogLogonTypes)
| where TerminalIPv6 !in (UnitedNetworks)
| extend UserandEmail = pack("ID", User, "Email", Email)
| summarize CountUsers = dcount(strcat(User, "_&_", Email)), Users = make_set(UserandEmail), StartTime = min(TimeGenerated), EndTime = max(TimeGenerated) 
    by SystemID, ClientID, TerminalIPv6
| where CountUsers > UsersperIP
| mv-expand Users 
| evaluate bag_unpack(Users, "User_")
| project SystemID, ClientID, column_ifexists("User_ID", ""), AccountCustomEntity = column_ifexists("User_Email", ""), IPCustomEntity = TerminalIPv6, StartTime, EndTime
| order by StartTime, EndTime desc

// Define variables
// Audit Log Classes - Failed Logons / Password Check
let AuditClasses = dynamic(['AUO', 'AU2', 'AU6', 'BU1']);
let perIPLimit = 6;
// Query logic
SAPAuditLog 
| where TimeGenerated {TimeSelect}
| where (SystemID in ({Systems}) or  '*' in ({Systems})) 
| where MessageID in (AuditClasses)
| extend DetailsBy = pack("User", User, "Email", Email, "SystemID", SystemID, "ClientID", ClientID)
| summarize LoginbyIPAttempts = count(), Details = make_set(DetailsBy), StartTime = min(TimeGenerated), EndTime = max(TimeGenerated) 
    by TerminalIPv6
// Check if number of login attempts per IP is higher than limit
| where LoginbyIPAttempts > perIPLimit 
| mv-expand Details
| evaluate bag_unpack(Details, "Details_")
| extend Details_User = column_ifexists("Details_User", "")
| project 
    StartTime, EndTime, IPCustomEntity = TerminalIPv6,
    AccountCustomEntity = column_ifexists("Details_Email", ""), Details_User,
    column_ifexists("Details_SystemID", ""),
    column_ifexists("Details_ClientID", "")
| summarize count() by Details_User

let AuditClasses = dynamic(['AU1','AU5']); // Audit Log Classes - Dialog Logon Successful, RFC Logon Successful
