| extend  locationString= strcat(tostring(LocationDetails["countryOrRegion"]), "/", 
tostring(LocationDetails["state"]), "/", tostring(LocationDetails["city"]), ";")
| summarize locationList = makeset(locationString), threeDayWindowLocationCount=dcount(locationString) by AppDisplayName, UserPrincipalName, 
timerange=bin(TimeGenerated, 3d)) on AppDisplayName, UserPrincipalName
| order by UserPrincipalName, timerange asc
| project timerange, AppDisplayName , UserPrincipalName, threeDayWindowLocationCount, locationList 
| order by AppDisplayName, UserPrincipalName, timerange asc
| extend timestamp = timerange, AccountCustomEntity = UserPrincipalName)
| extend Risk= 'Anomalous sign-in location by user account and authenticating application';


// Failed service logon attempt by user account with available AuditData
let failLimit = 10;
let ipLimit = 3;
let failedSignins = SigninLogs
| where TimeGenerated between(starttime..endtime)
| where ResultType != "0" and AppDisplayName != "Windows Sign In"
| extend UserPrincipalName = tolower(UserPrincipalName)
| extend CityState = strcat(tostring(LocationDetails.city),"|", tostring(LocationDetails.state))
| extend Result = strcat(ResultType,"-",ResultDescription)
| summarize StartTimeUtc = min(TimeGenerated), EndTimeUtc = max(TimeGenerated), DistinctIPAddressCount = dcount(IPAddress), IPAddresses = makeset(IPAddress),
CityStates = makeset(CityState), DistinctResultCount = dcount(Result), Results = makeset(Result), AppDisplayNames = makeset(AppDisplayName),
FailedLogonCount = count() by Type, OperationName, Category, UserPrincipalName = tolower(UserPrincipalName), ClientAppUsed, Location, CorrelationId
| project Type, StartTimeUtc, EndTimeUtc, OperationName, Category, UserPrincipalName, AppDisplayNames, DistinctIPAddressCount, IPAddresses, DistinctResultCount,
Results, FailedLogonCount, Location, CityStates
| where FailedLogonCount >= failLimit or DistinctIPAddressCount >= ipLimit
| extend Activity = pack("IPAddresses", IPAddresses, "AppDisplayNames", AppDisplayNames, "Results", Results, "Location", Location, "CityStates", CityStates)
| project Type, StartTimeUtc, EndTimeUtc, OperationName, Category, UserPrincipalName, FailedLogonCount, DistinctIPAddressCount, DistinctResultCount, Activity
| extend AccountCustomEntity = UserPrincipalName;
let accountMods = AuditLogs | where TimeGenerated >= auditLookback
| where Category == "UserManagement" or Category == "GroupManagement"
| extend ModProps = TargetResources.[0].modifiedProperties
| extend InitiatedBy = case(
isnotempty(tostring(parse_json(tostring(InitiatedBy.user)).userPrincipalName)), tostring(parse_json(tostring(InitiatedBy.user)).userPrincipalName),
isnotempty(tostring(parse_json(tostring(InitiatedBy.app)).displayName)), tostring(parse_json(tostring(InitiatedBy.app)).displayName),
"")
| extend UserPrincipalName = tolower(tostring(TargetResources.[0].userPrincipalName))
| mvexpand ModProps
| extend PropertyName = tostring(ModProps.displayName), oldValue = tostring(ModProps.oldValue), newValue = tostring(ModProps.newValue)
| extend ModifiedProps = pack("PropertyName",PropertyName,"oldValue",oldValue,"newValue",newValue)
| summarize StartTimeUtc = min(TimeGenerated), EndTimeUtc = max(TimeGenerated), Activity = make_bag(ModifiedProps) by Type, InitiatedBy, UserPrincipalName, Category, OperationName, CorrelationId, Id
| extend AccountCustomEntity = UserPrincipalName, timestamp= StartTimeUtc;
// Gather only Audit data for UserPrincipalNames that we have Audit data for
let accountNameOnly = failedSignins | project UserPrincipalName;
let auditMods = accountNameOnly
| join kind= innerunique (
accountMods
) on UserPrincipalName;
let availableAudits = auditMods | project UserPrincipalName;
let signinsWithAudit = availableAudits
| join kind= innerunique (
failedSignins
) on UserPrincipalName;
// Union the Current Signin failures so we do not lose them with the Auditing data we do have
let activity = (union isfuzzy=true
signinsWithAudit, auditMods)
| order by StartTimeUtc, UserPrincipalName;

let FailedServiceLogon= (activity
| project StartTimeUtc, EndTimeUtc, DataType = Type, Category, OperationName, UserPrincipalName, InitiatedBy, Activity, FailedLogonCount, DistinctIPAddressCount, DistinctResultCount, CorrelationId, Id
| order by UserPrincipalName, StartTimeUtc
| extend timestamp = StartTimeUtc, AccountCustomEntity = UserPrincipalName)
| extend Risk= 'Failed service logon attempt by user account with available AuditData';

UserGrantedGrants | union AnomalSiginLocAuth, FailedServiceLogon
| where UserPrincipalName =~ "{EntityToCheck}" or FirstInitiatedBy=~ "{EntityToCheck}" or FirstTargetUserName=~ "{EntityToCheck}" or SecondInitiatedBy=~ "{EntityToCheck}" or SecondTargetUserName=~ "{EntityToCheck}"
| project Risk, timestamp, ExtendedDetails= pack_all(true)
| order by Risk

let SelectedSystems= SAPSystems(SelectedSystemRoles=dynamic("{SystemRoles}")
, SelectedSystems=todynamic("[{Systems}]")
, SelectedSystemUsage= dynamic("{SystemUsage}")) | project SystemID, SystemRole;
let UserAssignments= SAPUsersAssignments() | project User, UserAuthGroup= UserGroupAuth;
let TimeStep= 4h;
let SelectedSeverities=  dynamic(["High", "Medium"]);//can also do//let SelectedSeverities =  dynamic(["All Severities"])
let SelectedRuleTypes= dynamic([ "Deterministic", "AnomaliesOnly"]); // Rule only goes for deterministic alerting
let AuditConfiguration= materialize(SAPAuditLogConfiguration(SelectedSeverities= SelectedSeverities, SelectedRuleTypes= SelectedRuleTypes)
| where SystemID in((SelectedSystems | project SystemID))
| project MessageID, SystemID, RolesTagsToExclude, Severity, Threshold, RuleType, CategoryName);
// get special users for exclusion and in-focus analysis
let SAPUsersGottenVIP = (SAPUsersGetVIP | project User = SAPUser, TagsList, SpecialFocusTagged);
let SAPUserRolesAndProfiles = (SAPUsersAssignments | project-keep User, SystemID, DirectRoles, ChildRoles, Profiles | summarize RolesAndProfiles = make_set(array_concat(DirectRoles, ChildRoles, Profiles), 120) by User, SystemID);
let RelevantEvents =SAPAuditLog
| where SystemID in((SelectedSystems | project SystemID))
| where isnotempty( User)
| where AlertSeverityText != "Low"
| where MessageID in ((AuditConfiguration | summarize count() by MessageID))
| lookup kind=inner (AuditConfiguration| project SystemID, MessageID, Severity, Threshold  ) on
$left.MessageID == $right.MessageID
,$left.SystemID == $right.SystemID
| where SelectedSeverities contains Severity
| make-series AuditEventsCounts= count() default=0 on TimeGenerated from startofday({TimeRange:start}) to {TimeRange:end} 
step TimeStep by SystemID, MessageID, User, Threshold;
RelevantEvents 
| extend crossedThreshold= series_greater(AuditEventsCounts, repeat(Threshold* TimeStep/1h, array_length(AuditEventsCounts)) )
| where array_sum(crossedThreshold) > 0
| extend crossedThreshold = array_iif(crossedThreshold,repeat(1, array_length(AuditEventsCounts)) , repeat(0, array_length(AuditEventsCounts)))
| lookup SAPUsersGottenVIP on User
| lookup SAPUserRolesAndProfiles on User, SystemID
| lookup (AuditConfiguration | project-keep SystemID, MessageID, RuleType, RolesTagsToExclude,  Severity) on MessageID, SystemID
| extend RolesProfilesAndTags = array_concat(RolesAndProfiles, TagsList)
| extend TagsIntersect = set_intersect(split(RolesTagsToExclude,";"), RolesProfilesAndTags)
| extend IntersectionSize = array_length(TagsIntersect)
// keep only non-excluded users that are not under "Special Focus"
| where IntersectionSize == 0 or isempty(IntersectionSize) or SpecialFocusTagged == true
| extend Anomalies= series_decompose_anomalies(AuditEventsCounts, Threshold=4, Test_point= 1)
| extend Anomaly= array_index_of(Anomalies,1)>= 0
| extend AnomalSeriesIndicator = iff(RuleType=="Deterministic", "Deterministic threshold crossed", iff(Anomaly, 'Anomalous audit event', 'Normal audit event'))
| mv-expand Anomalies to typeof(double), TimeGenerated to typeof(datetime), crossedThreshold to typeof(int )
| where (crossedThreshold > 0) and (( RuleType=="Deterministic") or ( RuleType== "AnomaliesOnly" and (Anomalies > 0 or "{AuditEventsType}" contains "All")))
| lookup UserAssignments on User
| summarize SuspiciousCount= floor((countif(Severity=="High") + countif(Severity=="Medium")*0.5),1) by UserAuthGroup, TimeGenerated
