var d = workflowContext.actions.Build_CVE_Table.outputs.body;

var regex = /(cve-\d+-\d+)/gi;  
  
return d.replace(regex, function(match) {  
    return '<a href="https://nvd.nist.gov/vuln/detail/' + match + '" target="_blank">' + match + '</a>';  
});

function flD(e, l) {
    v = e.node.compliance.cveFindings.map((f) => {
      return {
        name: e.node.name,
        ipAddress: e.node.ipAddress,
        cveId: f.cveId,
        cvssScoreV3: f.cvssScoreV3,
        severity: f.severity,
        summary:
          f.summary.replace(/[^a-z0-9.-_, ]/gi,'').substr(0, 70)+
          (f.summary.length > 70 ? "..." : ""),
      };
    });
    return srtD(v).slice(0,l)
  }

function srtD(fld) {
  fld.sort((a, b) => {
    if (a.cvssScoreV3 !== b.cvssScoreV3) {
      return b.cvssScoreV3 - a.cvssScoreV3; // Descending order by cvssScoreV3
    } else {
      // Descending order by CVE ID year
      let yearA = parseInt(a.cveId.split("-")[1]);
      let yearB = parseInt(b.cveId.split("-")[1]);
      return yearB - yearA;
    }
  });

  return fld;
}

let e = workflowContext.actions.Parse_endpoints_array.outputs.body;

return e.map((item) => flD(item, 20))

