var d = workflowContext.actions.Flatten_API_Gateway_endpoints.outputs.body;
const gT = (d) => {      
    if (!d || d.length === 0) return '';    
    let iK = [...new Set([].concat(...d.map(e => Object.keys(e[0]))))];

    let table = `<table><thead><tr>${iK.map(k => `<th>${k}</th>`).join('')}</tr></thead><tbody>`;  
    iK = iK.slice(2,10);
    
    let count = table.length;  
    for (let i of d) {  
        let rows = i.map(r => {return "<tr><td>" + iK.map(k => {return r[k]} ).join("</td><td>") + "</td></tr>" })
        rows[0] = rows[0].replace('<tr><td>', `<tr><td rowspan="${rows.length}">${i[0]["name"]}</td><td rowspan="${rows.length}">${i[0]["ipAddress"]}</td><td>`)
        for (let r of rows) {
            if (count + r.length > 28000) break;
            table += r;  
            count += r.length;
        }          
    }  
    return table + '</tbody></table>';  
};
return gT(d)

var e = workflowContext.actions.Parse_endpoints_array.outputs.body;

function sensorReadings(r) {
	return r.columns.map(c => {
		if (c.sensor && c.sensor.name) {
			return { name: [c.sensor.name, c.name].join(' - '), value: c.values.join(' ') };
		} else {
			return { name: c.name, value: c.values.join(' ') };
		}
	});
}

function flatten(obj, pk = '') {
	if (pk !== '') pk += ' ';
	let flat = {};
	Object.keys(obj).forEach((key) => {
		if (key === 'sensorReadings') {
			sensorReadings(obj[key]).forEach((r) => {
				flat[r.name] = r.value;
			});
		} else {
			if (typeof obj[key] === 'object' && obj[key] !== null) {
				Object.assign(flat, flatten(obj[key], pk + key));
			} else {
				flat[pk + key] = obj[key];
			}
		}
	});
	return flat;
}

return e.map(function(e) { return flatten(e.node); });

