query ($incidentHosts: EndpointFieldFilter, $endCursor: Cursor, $source: EndpointSource) {
  endpoints(
    source: $source
    first: 10
    after: $endCursor
    filter: $incidentHosts
  ) {
    edges {
      node {
        name
        ipAddress
        os { platform }
      }
    }
    pageInfo {
      hasNextPage
      endCursor
    }
  }
}

