var e = workflowContext.actions.Parse_the_array_endpoints.outputs.body;

function sensorReadings(r) {
	return r.columns.map(c => {
		if (c.sensor && c.sensor.name) {
			return { name: [c.sensor.name, c.name].join(' - '), value: c.values.join(' ') };
		} else {
			return { name: c.name, value: c.values.join(' ') };
		}
	});
}

function flatten(obj, pk = '') {
	if (pk !== '') pk += ' ';
	var flat = {};
	Object.keys(obj).forEach((key) => {
		if (key === 'sensorReadings') {
			sensorReadings(obj[key]).forEach((r) => {
				flat[r.name] = r.value;
			});
		} else {
			if (typeof obj[key] === 'object' && obj[key] !== null) {
				Object.assign(flat, flatten(obj[key], pk + key));
			} else {
				flat[pk + key] = obj[key];
			}
		}
	});
	return flat;
}

return e.map(function(e) { return flatten(e.node); });

var action_results = workflowContext.actions.Get_action_result.outputs.body.data;
var action_id = workflowContext.actions.Parse_current_issued_action.outputs.body.id;
var columns = action_results.result_sets[0].columns.map(c => c.name);
var robjects = [];

action_results.result_sets.forEach(function(rs) {
	rs.rows.forEach(function(row) {
		var robject = {'action id': action_id};
		columns.forEach(function(c, i) {
			robject[c] = row.data[i][0].text;
		});
		robjects.push(robject);
	});
});

return robjects;
