query ($incidentHosts: EndpointFieldFilter, $endCursor: Cursor, $source: EndpointSource) {
  endpoints(source: $source, first: 10, after: $endCursor, filter: $incidentHosts) {
    edges {
      node {
        name
        ipAddress
        sensorReadings(sensors: [{name: "SCCM Client Health"}]) {
          columns {
            sensor {
              name
            }
            name
            values
          }
        }
      }
    }
    pageInfo {
      hasNextPage
      endCursor
    }
  }
}
