variables.value:
query ($endpointsFilter: EndpointFieldFilter, $endCursor: Cursor, $source: EndpointSource, $refreshCursor: Cursor) {
  endpoints(source: $source, first: 10, after: $endCursor, filter: $endpointsFilter, refresh: $refreshCursor) {
    edges {
      node {
        name
        ipAddress
        os { platform }
        sensorReadings(
          sensors: [
            {
              name: "Patch - Patch List Applicability",
              params: [{name: "bin", value: "0"}, {name: "binCount", value: "1"}],

            }
          ]
        ) {
          columns {
            sensor {
              name
            }
            name
            values
          }
        }
      }
    }
    collectionInfo {
      startCursor
      success
    }
    pageInfo {
      hasNextPage
      endCursor
    }
  }
}
