body.messageBody:
{
    "type": "AdaptiveCard",
    "body": [
  {
    "size": "large",
    "text": "Suspicious Device - Microsoft Sentinel",
    "type": "TextBlock",
    "weight": "bolder",
    "wrap": true
  },
  {
    "text": "Possible comprised device detected by the provider :  @{outputs('Compose_product_name')}",
    "type": "TextBlock",
    "wrap": true
  },
  {
    "text": " @{triggerBody()?['object']?['properties']?['severity']} Incident - Carbon Black Device Actions ",
    "type": "TextBlock",
    "weight": "Bolder",
    "wrap": true
  },
  {
    "text": " Incident No: @{triggerBody()?['object']?['properties']?['incidentNumber']}  ",
    "type": "TextBlock",
    "weight": "Bolder",
    "wrap": true
  },
  {
    "text": "Incident description",
    "type": "TextBlock",
    "weight": "Bolder",
    "wrap": true
  },
  {
    "text": "@{triggerBody()?['object']?['properties']?['description']}",
    "type": "TextBlock",
    "wrap": true
  },
  {
    "text": "[Click here to view the Incident](@{triggerBody()?['object']?['properties']?['incidentUrl']})",
    "type": "TextBlock",
    "wrap": true
  },
        {
            "type": "TextBlock",

            "weight": "Bolder",
            "text": "Below is the summary of actions taken on Devices by SOC",
            "wrap": true
        },
    {
  "columns": [
    {
      "items": @{body('Select_action_summary')},
      "type": "Column",
      "wrap": true
    }
  ],
  "separator": "true",
  "type": "ColumnSet",
  "width": "stretch"
}
 ],
"width":"auto",
 "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
 "version": "1.2"
}
