body.messageBody:
{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "TextBlock",
            "text": "Vectra Mark Detections As Fixed",
            "style": "heading",
            "horizontalAlignment": "center",
            "color": "accent",
            "size": "medium",
            "weight": "bolder",
            "id": "title",
            "wrap": true
        },
        {
            "type": "TextBlock",
            "text": "Entity ID =  @{variables('entity_id')}\n\nEntity Type= @{variables('entity_type')}",
            "wrap": true,
            "style": "default",
            "color": "accent",
            "separator": true,
            "id": "x"
        },
        {
            "type": "Input.ChoiceSet",
            "id": "choice",
            "choices": [
                {
                    "title": "ALL",
                    "value": "all"
                },
                {
                    "title": "Selected Detections",
                    "value": "selected"
                }
            ],
            "isRequired": true,
            "label": "Please select option for detections to mark them fixed.",
            "separator": true,
            "errorMessage": "Please select atleast one option."
        },
        {
            "type": "ActionSet",
            "actions": [
                {
                    "type": "Action.Submit",
                    "title": "Submit Answer",
                    "id": "submit",
                    "style": "positive"
                }
            ]
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.3"
}
