body.Collect Alarms:
@if(
 or(
  empty(body('Parse_current_VONE_server')?['properties.itemsKeyValue']?['Collect Alarms']), 
  equals(body('Parse_current_VONE_server')?['properties.itemsKeyValue']?['Collect Alarms'], null), 
  equals(body('Parse_current_VONE_server')?['properties.itemsKeyValue']?['Collect Alarms'], '')
 ),
 'true', 
 body('Parse_current_VONE_server')?['properties.itemsKeyValue']?['Collect Alarms']
)

body.Key Vault Password ID:
@if(
 or(
  empty(body('Parse_current_VONE_server')?['properties.itemsKeyValue']?['Key Vault Password ID']), 
  equals(body('Parse_current_VONE_server')?['properties.itemsKeyValue']?['Key Vault Password ID'], null), 
  equals(body('Parse_current_VONE_server')?['properties.itemsKeyValue']?['Key Vault Password ID'], '')
 ),
 concat(body('Parse_current_VONE_server')?['properties.itemsKeyValue']?['Veeam Server Name'], 'Password'), 
 body('Parse_current_VONE_server')?['properties.itemsKeyValue']?['Key Vault Password ID']
)
